-- Quest: canon-quest-washington-dc-2020-2029-vietnam-memorial
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-washington-dc-2020-2029-vietnam-memorial', 'Вашингтон 2020-2029 — «Мемориал Вьетнама»', 'Игрок проходит вдоль V-образной стены, ищет конкретные имена, поддерживает ветеранов и фиксирует эмоциональные отклики.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-washington-dc-2020-2029-vietnam-memorial", "title": "Вашингтон 2020-2029 — «Мемориал Вьетнама»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "quest", "vietnam-war"], "topics": ["memorials", "emotional-design"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "tourism-service", "education-service"], "related_documents": [{"id": "github-issue-1033", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1033", "relation": "migrated_to", "migrated_at": "2025-11-23T02:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/washington-dc/2020-2029/quest-006-vietnam-memorial.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Первичный текст описывал мемориал поверхностно и не раскрывал эмоциональную механику, связанную с памятью о войне.", "goal": "Создать квест переживания, где игрок взаимодействует с именами погибших и помогает ветеранам в ритуалах памяти.", "essence": "Игрок проходит вдоль V-образной стены, ищет конкретные имена, поддерживает ветеранов и фиксирует эмоциональные отклики.", "key_points": ["Черный гранит отражает посетителей, а 58 318 имён формируют интерактивный список памяти.", "Игрок может оставить подношения или помочь другим NPC сделать отпечаток имени.", "Квест поднимает моральные дилеммы и влияет на отношение фракций, связанных с войной."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Мемориал ветеранов Вьетнама — место скорби и памяти. Игрок исследует архитектуру, отражающую собственное лицо,\\nи выполняет ритуалы, позволяющие пережить события войны через взгляд ветеранов и семей погибших.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "experience", "title": "Этапы переживания", "body": "1. Войти на территорию и почувствовать переход от шумного National Mall к тишине мемориала.\\n2. Просканировать стену, найти имена через базу данных и сопроводить NPC к нужной панели.\\n3. Помочь сделать отпечаток имени на бумаге и оставить цветы или письма.\\n4. Взаимодействовать с ветеранами, выслушивая истории и поддерживая их эмоциональные шкалы.\\n5. Завершить визит медитацией у отражающего гранита и записью в дневник.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "emotional_responses", "title": "Эмоциональные реакции", "body": "- Игрок балансирует между показателями эмпатии и устойчивости, чтобы не получить негативные эффекты.\\n- Возможность активировать кат-сцену с ветераном, вспоминающим конкретное сражение.\\n- Установка мемориальных свечей и управление временем суток создают уникальные визуальные сцены.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 3 000 XP, ресурс эмоций «Скорбь», финансовых затрат нет.\\n- Ачивка «Память войны», открывающая дополнительные ветеранские миссии.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Мемориальные ритуалы с отслеживанием эмоциональных метрик.\\n- Система подношений и писем, влияющая на состояние стены и реакции NPC.\\n- Исторические фрагменты, которые добавляются в личную хронику персонажа.\\n", "mechanics_links": ["mechanics/exploration/exploration-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает военную линию Вашингтона и связывает её с миссиями о государственных решениях,\\nпосттравматических программах и отношениях с ветеранами.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1033, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Мемориал Вьетнама» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

