-- Quest: canon-quest-washington-dc-2020-2029-pentagon
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-washington-dc-2020-2029-pentagon', 'Вашингтон 2020-2029 — «Пентагон»', 'Игрок получает допуск, проходит проверку, изучает коридоры военной машины США и участвует в мемориальных ритуалах.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-washington-dc-2020-2029-pentagon", "title": "Вашингтон 2020-2029 — «Пентагон»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "quest", "pentagon"], "topics": ["military-infrastructure", "homeland-security"], "related_systems": ["narrative-service", "social-service", "ops-service"], "related_documents": [{"id": "github-issue-1034", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1034", "relation": "migrated_to", "migrated_at": "2025-11-23T02:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/washington-dc/2020-2029/quest-007-pentagon.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о посещении Пентагона не учитывал ограничения безопасности и возможности взаимодействия с военной бюрократией.", "goal": "Добавить структуру визита в крупнейшее офисное здание мира, интегрировать 9/11 мемориал и показать работу Министерства обороны.", "essence": "Игрок получает допуск, проходит проверку, изучает коридоры военной машины США и участвует в мемориальных ритуалах.", "key_points": ["Пятиугольная архитектура и гигантские коридоры создают уникальный навигационный опыт.", "Внутренние туры требуют согласования, поднимая Stakes безопасности.", "Внешний мемориал 9/11 добавляет эмоциональный финал."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Пентагон — символ военной мощи США. Игрок пересекает реку в Арлингтон, знакомится с протоколами безопасности,\\nпосещает Hall of Heroes и завершает визит у мемориала 9/11.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "access", "title": "Допуск и маршрут", "body": "1. Подать заявку на тур и получить временный пропуск с проверкой биометрии.\\n2. Пройти многоуровневый досмотр и инструктаж по поведению внутри объекта.\\n3. Войти в центральный коридор, изучить пятилучевую структуру и ориентиры для навигации.\\n4. Посетить Hall of Heroes и музей внутри, разблокируя сведения о ключевых операциях.\\n5. Завершить тур в 9/11 Memorial Chapel внутри здания.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "security", "title": "Безопасность и взаимодействия", "body": "- Игрок соблюдает строгие правила, рискуя потерять допуск при нарушении.\\n- Возможность диалогов с офицерами, раскрывающих фракции и текущие военные программы.\\n- Хакинг-сцены исключены, но возможно наблюдение за логистикой и сбор открытых данных.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "memorial", "title": "Мемориальный контекст", "body": "- Внешний мемориал 9/11 с 184 скамьями позволяет провести ритуал памяти.\\n- Игрок может сопровождать родственников и помочь им оставить подношения.\\n- Отражения в водной глади усиливают эмоциональное впечатление и открывают кат-сцену.\\n", "mechanics_links": ["mechanics/exploration/exploration-systems.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 500 XP, военный ресурс +25, финансовых затрат нет.\\n- Ачивка «Свидетель Пентагона», повышающая доверие военных NPC и открывающая контракты.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест интегрируется с военной линией Вашингтона, будущими операциями спецслужб и сюжетами о\\nпосттравматической поддержке семей жертв 9/11.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1034, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Пентагон» в knowledge-entry формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

