-- Quest: canon-quest-washington-dc-2020-2029-supreme-court
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-washington-dc-2020-2029-supreme-court', 'Вашингтон 2020-2029 — «Верховный суд США»', 'Игрок посещает здание, изучает архитектуру и при возможности наблюдает заседание, формируя репутацию в юридическом сообществе.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-washington-dc-2020-2029-supreme-court", "title": "Вашингтон 2020-2029 — «Верховный суд США»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "quest", "supreme-court"], "topics": ["legal-system", "civic-education"], "related_systems": ["narrative-service", "social-service", "political-service"], "related_documents": [{"id": "github-issue-1035", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1035", "relation": "migrated_to", "migrated_at": "2025-11-23T02:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/washington-dc/2020-2029/quest-008-supreme-court.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о Верховном суде требовал структурирования и подсветки судебной власти как отдельного игрового направления.", "goal": "Организовать визит в Supreme Court с акцентом на процедуры слушаний и политическое влияние решений.", "essence": "Игрок посещает здание, изучает архитектуру и при возможности наблюдает заседание, формируя репутацию в юридическом сообществе.", "key_points": ["Девять пожизненно назначенных судей — важная часть баланса власти.", "Надпись «Equal Justice Under Law» и архитектура греческого храма усиливают символизм.", "Возможность попасть на слушания создаёт уникальный игровой момент и влияет на политические ветви."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Верховный суд — вершина судебной системы США. Игрок попадает внутрь, знакомится с судами и\\nразрабатывает стратегию влияния на будущие правовые сюжетные линии.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "visit_flow", "title": "Этапы визита", "body": "1. Войти на площадь перед зданием, изучить архитектуру и символику.\\n2. Пройти через контроль безопасности и посетить зал Courtroom.\\n3. Ознакомиться с экспозицией о работе суда и статуей Lady Justice.\\n4. Попасть на слушание (если очередь позволяет) и анализировать аргументы сторон.\\n5. Завершить визит в библиотеке, получив доступ к цифровым архивам.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "legal_engagement", "title": "Юридические взаимодействия", "body": "- Игрок ведёт записи, которые повлияют на политические квесты и отношение фракций.\\n- Возможность задать вопросы сотрудникам о прецедентах и о процессе выборов судей.\\n- Система репутации отслеживает уважение к процедурам и корректность поведения.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 000 XP, юриспруденция +20, финансовых затрат нет.\\n- Ачивка «Свидетель правосудия», открывающая доступ к линейкам политических реформ.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Слушания с интерактивным выбором позиций и последующим влиянием на законы.\\n- Сбор юридических документов и цитат для будущих прецедентов.\\n- Управление временем в очередях и взаимодействие с общественными группами.\\n", "mechanics_links": ["mechanics/exploration/exploration-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает юридическую линию Вашингтона и соединяет её с кампаниями о гражданских правах,\\nконституционных поправках и балансах ветвей власти.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1035, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Верховный суд США» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

