-- Quest: canon-quest-washington-dc-2020-2029-cherry-blossoms
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-washington-dc-2020-2029-cherry-blossoms', 'Вашингтон 2020-2029 — «Цветение сакуры у Tidal Basin»', 'Игрок планирует посещение пика цветения, исследует Tidal Basin и документирует символику подарка Японии.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-washington-dc-2020-2029-cherry-blossoms", "title": "Вашингтон 2020-2029 — «Цветение сакуры у Tidal Basin»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:15:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "festival", "nature"], "topics": ["urban-tourism", "seasonal-events"], "related_systems": ["narrative-service", "event-service"], "related_documents": [{"id": "github-issue-1036", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1036", "relation": "migrated_to", "migrated_at": "2025-11-23T02:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/washington-dc/2020-2029/quest-009-cherry-blossoms.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В природной ветке Вашингтона не было полноценного сценария цветения сакуры и фестиваля у Tidal Basin.", "goal": "Описать сезонное событие National Cherry Blossom Festival и создать туристический маршрут.", "essence": "Игрок планирует посещение пика цветения, исследует Tidal Basin и документирует символику подарка Японии.", "key_points": ["Цветение длится около двух недель и зависит от ежедневного прогноза.", "Более 3 000 деревьев сакуры были подарены Японией в 1912 году.", "Jefferson Memorial и набережная Tidal Basin формируют iconic-фото локации."]}, "content": {"sections": [{"id": "season_briefing", "title": "Подготовка к сезону", "body": "Квест стартует за несколько дней до пика цветения. Игрок отслеживает прогноз от National Park Service, бронирует окна\\nпосещения и получает гайд по передвижению внутри зоны Tidal Basin.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "festival_flow", "title": "Маршрут фестиваля", "body": "1. Прибытие к Tidal Basin ранним утром, чтобы избежать толпы и сделать панорамные снимки.\\n2. Прогулка вокруг Jefferson Memorial и Martin Luther King Jr. Memorial с фиксацией сакуры.\\n3. Участие в активности фестиваля (парад, уличные перформансы, сувенирные рынки).\\n4. Создание фото-серии и отчёта для туристической сети LiveOps.\\n5. Ночная подсветка и вечерняя прогулка вдоль Basin.\\n", "mechanics_links": [], "assets": []}, {"id": "cultural_context", "title": "Культурный контекст", "body": "- Подарок Японии из 3 020 деревьев укрепил дипломатические отношения в 1912 году.\\n- Фестиваль символизирует дружбу и обновление, а также привлекает сотни тысяч посетителей.\\n- Пик цветения длится 4–7 дней и сильно зависит от погодных условий.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 1 500 XP.\\n- Атрибут «Эстетика» +25.\\n- Достижение «Свидетель цветения».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Сезонные события и управление расписанием.\\n- Туристические активности и создание пользовательского контента.\\n- Социальные взаимодействия с NPC-гидами и фестивальными организаторами.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает природную линию Вашингтона с туристическими событиями National Mall и открывает миссии LiveOps по\\nсезонным фестивалям.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1036, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Цветение сакуры у Tidal Basin» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

