INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-golden-gate', 'Киев 2020-2029 — «Золотые ворота»', 'Игрок посещает реконструированные ворота, проживает историю через VR и исследует подземелья с артефактами Руси.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-golden-gate", "title": "Киев 2020-2029 — «Золотые ворота»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T02:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["kiev", "golden-gate", "history"], "topics": ["heritage", "exploration"], "related_systems": ["narrative-service", "exploration-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-kiev-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/kiev/2020-2029/quest-006-golden-gate.md", "visibility": "internal", "audience": ["lore", "narrative", "tourism"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновой текст не связывал исторический контент Золотых ворот с игровыми событиями и последствиями.", "goal": "Создать квест исследование, комбинирующий VR-реконструкцию Киева XI века и поиски тайного хода.", "essence": "Игрок посещает реконструированные ворота, проживает историю через VR и исследует подземелья с артефактами Руси.", "key_points": ["Связь с князем Ярославом Мудрым и культурой Киевской Руси.", "Визуализация древнего города через VR-тур.", "Тайный ход открывает эксплоринг и награды культурного профиля."]}, "content": {"sections": [{"id": "arrival", "title": "Посещение ворот", "body": "Игрок прибывает к реконструированным Золотым воротам, узнаёт об истории строительства 1037 года и осматривает церковь\\nБлаговещения, расположенную над входом. Экскурсовод даёт доступ к VR-модулям.\\n", "mechanics_links": [], "assets": []}, {"id": "vr", "title": "VR-экскурсия", "body": "Активируется VR-сцена «Киев XI века»: деревянные дома, княжеские кортежи и укрепления. Игрок может наблюдать город глазами\\nЯрослава Мудрого и принимать исторические решения, влияющие на культурную репутацию.\\n", "mechanics_links": [], "assets": []}, {"id": "underground", "title": "Тайный ход", "body": "После VR-сеанса игрок обнаруживает вход в подземелья. Исследование древних коридоров приносит артефакты Руси и открывает\\nдополнительную линию эксплоринга.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "1 500 XP, +10 к параметру «Культура» и шанс получить древние артефакты. История вписывается в историческую линию Киева.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Золотые ворота", "definition": "Главные ворота древнего Киева, возведённые при князе Ярославе Мудром в XI веке."}], "references": [{"title": "Golden Gate of Kyiv", "link": "https://www.encyclopediaofukraine.com/display.asp?linkpath=pages/g/o/goldengate.htm"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Золотые ворота» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards