-- Quest: canon-quest-washington-dc-2020-2029-politics-lobbying
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-washington-dc-2020-2029-politics-lobbying', 'Вашингтон 2020-2029 — «Политика и лоббизм»', 'Игрок погружается в офисы лоббистов, анализирует денежные потоки и принимает решение — играть по правилам или раскрыть систему.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-washington-dc-2020-2029-politics-lobbying", "title": "Вашингтон 2020-2029 — «Политика и лоббизм»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:15:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["washington-dc", "politics", "lobbying"], "topics": ["governance", "power-structures"], "related_systems": ["narrative-service", "politics-service", "economy-system"], "related_documents": [{"id": "github-issue-1037", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1037", "relation": "migrated_to", "migrated_at": "2025-11-23T02:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/washington-dc/2020-2029/quest-010-politics-lobbying.md", "visibility": "internal", "audience": ["concept", "narrative", "politics"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Политическая линия Вашингтона не объясняла механику лоббизма и влияние района K Street.", "goal": "Показать, как корпоративные бюджеты трансформируются в политические решения и моральные дилеммы.", "essence": "Игрок погружается в офисы лоббистов, анализирует денежные потоки и принимает решение — играть по правилам или раскрыть систему.", "key_points": ["На K Street сосредоточены ключевые лоббистские фирмы, ежегодно распределяющие миллиарды долларов.", "Более 10 000 зарегистрированных лоббистов сопровождены аналитиками и юристами, пишущими законопроекты.", "Квест предлагает моральный выбор, влияющий на репутацию и доступ к политическим миссиям."]}, "content": {"sections": [{"id": "k_street_orientation", "title": "Знакомство с K Street", "body": "Первая сцена знакомит игрока со схемой влияния: карта офисов, список клиентов и аналитический дашборд пожертвований. Игрок\\nучится читать отчёты FARA и готовит встречу с менеджером фирмы.\\n", "mechanics_links": ["mechanics/politics/lobbying-influence-network.yaml"], "assets": []}, {"id": "funding_flow", "title": "Потоки финансирования", "body": "Игрок анализирует пожертвования и PAC, встречается с лоббистом, который показывает, как формируются talking points для\\nзаконодателей. Подготовка аналитической записки даёт бонус доверия.\\n", "mechanics_links": ["mechanics/economy/cost-of-living.yaml"], "assets": []}, {"id": "moral_choice", "title": "Моральный выбор", "body": "Финальный акт — конфликт интересов. Можно поддержать клиентский законопроект и получить доступ к ресурсам, либо передать\\nдокументы журналистам, снизив влияние корпораций. Решение изменяет репутацию среди фракций на Капитолийском холме.\\n", "mechanics_links": ["mechanics/narrative/branching-decisions.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 5 000 XP.\\n- Атрибут «Политика» +30.\\n- Достижение «Знаток системы».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Политические симуляции влияния и репутации.\\n- Экономическая аналитика и управление ресурсами.\\n- Социальные взаимодействия с NPC из Конгресса, корпораций и медиа.\\n", "mechanics_links": ["mechanics/politics/political-influence-meter.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает политическую линию Вашингтона с сюжетами о реформе финансирования кампаний и кампаниями LiveOps по\\nпрозрачности власти.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1037, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Политика и лоббизм» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

