INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-delhi-2020-2029-taj-mahal-trip', 'Дели 2020-2029 — «Поездка к Тадж-Махалу»', 'Игрок планирует дорогу, исследует архитектурные детали и переживает романтическое наследие Шах-Джахана.', 'main', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "plan_trip_to_agra", "text": "Получить доступ к расписаниям поездов и шоссе Yamuna Expressway, подобрать оптимальное окно выезда, оформить билеты и забронировать гида в Агре", "type": "travel", "target": "delhi_taj_mahal_trip_planning", "count": 1, "optional": false}, {"id": "enter_taj_mahal_complex", "text": "Войти на территорию комплекса, пройти security-проверку, осмотреть главный купол, симметричные сады и отражающие бассейны", "type": "explore", "target": "delhi_taj_mahal_exploration", "count": 1, "optional": false}, {"id": "study_architectural_details", "text": "Изучить инкрустации semi-precious stones и каллиграфию на арках", "type": "research", "target": "delhi_taj_mahal_architecture", "count": 1, "optional": false}, {"id": "observe_marble_color_change", "text": "Наблюдать рассвет или закат для наблюдения изменения цвета мрамора", "type": "observe", "target": "delhi_taj_mahal_sunrise_sunset", "count": 1, "optional": false}]'::jsonb, '{"experience": 5000, "currency": -50, "items": [], "reputation": {"delhi_cultural": 30}, "flags": ["witness_eternal_love_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-delhi-2020-2029-taj-mahal-trip", "title": "Дели 2020-2029 — «Поездка к Тадж-Махалу»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["delhi", "taj-mahal", "architecture"], "topics": ["cultural-heritage", "iconic-landmarks"], "related_systems": ["narrative-service", "travel-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/delhi/2020-2029/quest-001-taj-mahal-trip.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристическая линия Дели не содержала детального сценария поездки к Тадж-Махалу.", "goal": "Создать маршрут из столицы в Агру с акцентом на историю мавзолея и его визуальные особенности.", "essence": "Игрок планирует дорогу, исследует архитектурные детали и переживает романтическое наследие Шах-Джахана.", "key_points": ["Поездка занимает 3–4 часа на поезде или автомобиле и требует подготовки по времени.", "Белый мрамор меняет оттенок в зависимости от освещения, формируя визитную карточку монумента.", "История любви императора и Мумтаз-Махал становится эмоциональным центром квеста."]}, "quest_definition": {"quest_type": "main", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "plan_trip_to_agra", "text": "Получить доступ к расписаниям поездов и шоссе Yamuna Expressway, подобрать оптимальное окно выезда, оформить билеты и забронировать гида в Агре", "type": "travel", "target": "delhi_taj_mahal_trip_planning", "count": 1, "optional": false}, {"id": "enter_taj_mahal_complex", "text": "Войти на территорию комплекса, пройти security-проверку, осмотреть главный купол, симметричные сады и отражающие бассейны", "type": "explore", "target": "delhi_taj_mahal_exploration", "count": 1, "optional": false}, {"id": "study_architectural_details", "text": "Изучить инкрустации semi-precious stones и каллиграфию на арках", "type": "research", "target": "delhi_taj_mahal_architecture", "count": 1, "optional": false}, {"id": "observe_marble_color_change", "text": "Наблюдать рассвет или закат для наблюдения изменения цвета мрамора", "type": "observe", "target": "delhi_taj_mahal_sunrise_sunset", "count": 1, "optional": false}], "rewards": {"experience": 5000, "currency": -50, "items": [], "reputation": {"delhi_cultural": 30}, "flags": ["witness_eternal_love_achievement"]}, "branches": []}, "content": {"sections": [{"id": "travel_planning", "title": "Планирование маршрута", "body": "Игрок получает доступ к расписаниям поездов и шоссе Yamuna Expressway, подбирает оптимальное окно выезда, оформляет\\nбилеты и бронирует гида в Агре. Система учитывает время прибытия к рассвету.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "taj_exploration", "title": "Исследование мавзолея", "body": "1. Вход на территорию комплекса и прохождение security-проверки.\\n2. Осмотр главного купола, симметричных садов и отражающих бассейнов.\\n3. Изучение инкрустаций semi-precious stones и каллиграфии на арках.\\n4. Рассвет или закат для наблюдения изменения цвета мрамора.\\n", "mechanics_links": [], "assets": []}, {"id": "heritage_context", "title": "Контекст наследия", "body": "- Строительство велось в 1632–1653 годах силами более 20 000 мастеров.\\n- Тадж-Махал включён в список современного наследия и охраняется UNESCO.\\n- История Шах-Джахана и Мумтаз-Махал подчёркивает эмоциональный тон миссии.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 5 000 XP.\\n- Финансовые расходы: -50.\\n- Атрибут «Культура» +30.\\n- Достижение «Свидетель вечной любви».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Туристические путешествия с управлением расписанием.\\n- Исторические брифинги и коллекция артефактов.\\n- Фотографические испытания для LiveOps.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает архитектурную линию Индии и открывает миссии о Могольской империи и маршрутах Золотого треугольника.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1018, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики культурного наследия и знаковых достопримечательностей."}, {"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Поездка к Тадж-Махалу» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

