INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-delhi-2020-2029-red-fort', 'Дели 2020-2029 — «Красный форт»', 'Игрок исследует фортификацию, посещает дворцы и наблюдает, как премьер-министр поднимает флаг в День независимости.', 'main', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "enter_lahore_gate", "text": "Прибыть к воротам Лахора, пройти охрану и получить гида по ключевым локациям", "type": "travel", "target": "delhi_red_fort_entrance", "count": 1, "optional": false}, {"id": "tour_palace_circuit", "text": "Посетить Diwan-i-Am и Diwan-i-Khas, осмотреть Nahr-i-Behisht (канал рая) и сады, посетить Жемчужную мечеть", "type": "explore", "target": "delhi_red_fort_palace_circuit", "count": 3, "optional": false}, {"id": "attend_independence_ceremony", "text": "Участвовать в мероприятиях 15 августа: поднятие флага, речь премьер-министра и массовые празднования, зафиксировать эмоции толпы", "type": "interact", "target": "delhi_red_fort_independence_day", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "currency": -5, "items": [], "reputation": {"delhi_history": 20}, "flags": ["red_fort_guardian_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-delhi-2020-2029-red-fort", "title": "Дели 2020-2029 — «Красный форт»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["delhi", "red-fort", "heritage"], "topics": ["cultural-heritage", "national-identity"], "related_systems": ["narrative-service", "travel-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/delhi/2020-2029/quest-002-red-fort.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Историческая линия Дели не отображала роль Красного форта как символа независимости и наследия Моголов.", "goal": "Погрузить игрока в архитектуру Lal Qila и национальные церемонии 15 августа.", "essence": "Игрок исследует фортификацию, посещает дворцы и наблюдает, как премьер-министр поднимает флаг в День независимости.", "key_points": ["Красный песчаник и базальтовые вставки создают уникальный визуальный образ комплекса.", "Ворота Лахора, Diwan-i-Am и Diwan-i-Khas демонстрируют роскошь Могольского двора.", "Ежегодное поднятие флага 15 августа подчёркивает связь с современной Индией."]}, "quest_definition": {"quest_type": "main", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "enter_lahore_gate", "text": "Прибыть к воротам Лахора, пройти охрану и получить гида по ключевым локациям", "type": "travel", "target": "delhi_red_fort_entrance", "count": 1, "optional": false}, {"id": "tour_palace_circuit", "text": "Посетить Diwan-i-Am и Diwan-i-Khas, осмотреть Nahr-i-Behisht (канал рая) и сады, посетить Жемчужную мечеть", "type": "explore", "target": "delhi_red_fort_palace_circuit", "count": 3, "optional": false}, {"id": "attend_independence_ceremony", "text": "Участвовать в мероприятиях 15 августа: поднятие флага, речь премьер-министра и массовые празднования, зафиксировать эмоции толпы", "type": "interact", "target": "delhi_red_fort_independence_day", "count": 1, "optional": false}], "rewards": {"experience": 1500, "currency": -5, "items": [], "reputation": {"delhi_history": 20}, "flags": ["red_fort_guardian_achievement"]}, "branches": []}, "content": {"sections": [{"id": "entry_sequence", "title": "Вход и ориентация", "body": "Игрок прибывает к воротам Лахора, проходит охрану и получает гида по ключевым локациям. В сценарий включено описание\\nстен из красного песчаника и оборонительных башен.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "palace_circuit", "title": "Дворцовый маршрут", "body": "1. Посещение Diwan-i-Am и Diwan-i-Khas с рассказами о приёмах Моголов.\\n2. Осмотр Nahr-i-Behisht (канал рая) и садов.\\n3. Посещение Жемчужной мечети и аудио-гид об имперской жизни.\\n", "mechanics_links": [], "assets": []}, {"id": "independence_ceremony", "title": "Церемония независимости", "body": "Квест предусматривает участие в мероприятиях 15 августа: поднятие флага, речь премьер-министра и массовые празднования.\\nИгрок фиксирует эмоции толпы и готовит репортаж для культурных миссий.\\n", "mechanics_links": ["mechanics/world/events/city-landmark-events.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 1 500 XP.\\n- Финансовые расходы: -5.\\n- Атрибут «История» +20.\\n- Достижение «Хранитель Красного форта».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Задействованные механики", "body": "- Исторические экскурсии и культурные события.\\n- Социальные взаимодействия с NPC-экскурсоводами и официальными лицами.\\n- Документирование для образовательных сценариев.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает историческую линию Дели с миссиями о независимости Индии и культурными маршрутам UNESCO.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1019, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена 