INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-delhi-2029-street-food-chaos', 'Дели 2020-2029 — Хаос уличной еды', 'Игрок проходит гастрономический маршрут, балансируя между острыми специями и безопасностью здоровья.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "reach_chandni_chowk", "text": "Добраться до Chandni Chowk и составить карту торговцев", "type": "travel", "target": "delhi_chandni_chowk_market", "count": 1, "optional": false}, {"id": "taste_street_food", "text": "Продегустировать чаат, паратха, джалеби и ласси, фиксируя реакции персонажа", "type": "consume", "target": "delhi_street_food_tasting", "count": 4, "optional": false}, {"id": "manage_health_risks", "text": "Управлять показателями остроты и чистоты, чтобы избежать «Delhi Belly»", "type": "perform", "target": "delhi_street_food_health_management", "count": 1, "optional": false}, {"id": "interact_with_vendors", "text": "Общаться с местными продавцами и получать рецепты, влияющие на репутацию", "type": "social", "target": "delhi_street_food_vendors", "count": 1, "optional": false}, {"id": "create_food_guide", "text": "Сформировать гастрономический гид для туристов и жителей", "type": "craft", "target": "delhi_street_food_guide", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "currency": -10, "items": [{"id": "street_food_buff", "name": "Бафф +20% HP на 6 часов", "quantity": 1}], "reputation": {"delhi_culinary": 10}, "flags": ["street_food_gourmet_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-delhi-2029-street-food-chaos", "title": "Дели 2020-2029 — Хаос уличной еды", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["delhi", "street-food", "culinary"], "topics": ["urban_life", "culinary_traditions"], "related_systems": ["narrative-service", "health-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/delhi/2020-2029/quest-003-street-food-chaos.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кулинарная линия Дели не отражала многообразие уличной еды и риски «Delhi Belly».", "goal": "Дать игроку вкусить хаос Chandni Chowk, оценить риск-вознаграждение и познакомить с культовыми блюдами.", "essence": "Игрок проходит гастрономический маршрут, балансируя между острыми специями и безопасностью здоровья.", "key_points": ["Chandni Chowk — сердце уличной еды со смешением ароматов и толп.", {"Классические блюда": "чаат, паратха, джалеби, ласси."}, "Риск расстройства желудка создаёт дополнительную драму."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "reach_chandni_chowk", "text": "Добраться до Chandni Chowk и составить карту торговцев", "type": "travel", "target": "delhi_chandni_chowk_market", "count": 1, "optional": false}, {"id": "taste_street_food", "text": "Продегустировать чаат, паратха, джалеби и ласси, фиксируя реакции персонажа", "type": "consume", "target": "delhi_street_food_tasting", "count": 4, "optional": false}, {"id": "manage_health_risks", "text": "Управлять показателями остроты и чистоты, чтобы избежать «Delhi Belly»", "type": "perform", "target": "delhi_street_food_health_management", "count": 1, "optional": false}, {"id": "interact_with_vendors", "text": "Общаться с местными продавцами и получать рецепты, влияющие на репутацию", "type": "social", "target": "delhi_street_food_vendors", "count": 1, "optional": false}, {"id": "create_food_guide", "text": "Сформировать гастрономический гид для туристов и жителей", "type": "craft", "target": "delhi_street_food_guide", "count": 1, "optional": false}], "rewards": {"experience": 1500, "currency": -10, "items": [{"id": "street_food_buff", "name": "Бафф +20% HP на 6 часов", "quantity": 1}], "reputation": {"delhi_culinary": 10}, "flags": ["street_food_gourmet_achievement"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест предлагает игроку погрузиться в шум и запахи старого Дели, попробовать легендарные блюда и принять решение,\\nсколько риска допустимо ради новых вкусов.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до Chandni Chowk и составить карту торговцев.\\n2. Продегустировать чаат, паратха, джалеби и ласси, фиксируя реакции персонажа.\\n3. Управлять показателями остроты и чистоты, чтобы избежать «Delhi Belly».\\n4. Общаться с местными продавцами и получать рецепты, влияющие на репутацию.\\n5. Сформировать гастрономический гид для туристов и жителей.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Уличная еда Дели сочетает острые специи, сладости и молочные напитки. Chandni Chowk известен своей плотностью киосков\\nи доступностью — блюда стоят 50–100 рупий.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP\\n- Расходы: 10 единиц валюты\\n- Бафф: +20% HP на 6 часов\\n- Риск: шанс 50% получить дебафф «Delhi Belly» (-30% HP)\\n- Ачивка: «Уличный гурман»\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест задействует кулинарные, социальные и риск-менеджмент механики, открывая доступ к фестивалям еды и рецептам.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с кулинарной линией Дели, миссиями о здоровье путешественников и сюжетами о традициях уличных рынков Индии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1020, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики городской жизни и кулинарных традиций."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертирован квест «Хаос уличной еды» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

