INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-baku-caspian-caviar', 'Баку — Каспийская икра', 'Игрок исследует производство деликатеса, пробует разные виды икры и понимает ценность Каспия.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-baku-caspian-caviar", "title": "Баку — Каспийская икра", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-12T02:13:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-12T02:13:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["baku", "gastronomy", "luxury"], "topics": ["regional-content", "economy"], "related_systems": ["economy-service", "food-system", "mood-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/baku/2020-2029/quest-004-caspian-caviar.md", "visibility": "internal", "audience": ["lore", "quest-design", "economy"], "risk_level": "medium"}, "review": {"chain": [{"role": "lore_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о чёрной икре не имел структурированной информации для экономических цепочек.", "goal": "Описать дегустацию икорной фермы и экономику роскоши.", "essence": "Игрок исследует производство деликатеса, пробует разные виды икры и понимает ценность Каспия.", "key_points": ["Подробный маршрут на легальную ферму и дегустация.", "Награды с баффом опыта и экономическими последствиями.", "Связь с эко-проблемами и защитой осетра."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: medium. Формат: solo. Длительность: 1–2 часа.\\nНаграды: 2,000 XP, -500 едди, бафф «Гурман» (+50% XP на 24 часа). Ачивка «Икорный Гурман».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Найти сертифицированную икорную ферму и получить доступ.\\n2. Изучить процесс разведения осетра и извлечения икры.\\n3. Провести дегустацию белужьей, осетровой и севрюжьей икры.\\n4. Приобрести банку в качестве инвестиции или подарка.\\n5. Обсудить темы браконьерства и охраны вида.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Каспийское море — уникальный ареал осетра. Чёрная икра — символ азербайджанской роскоши и наследия СССР.\\nИкорные фермы заботятся о долгосрочной защите вида.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "economy-service (роскошный товар), food-system (деликатес), mood-system (бафф «Эстетический восторг»). Возможны контракты на поставку для VIP-клиентов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "2,000 XP, -500 едди, бафф «Гурман» (+50% XP на 24 ч), ачивка «Икорный Гурман».\\nОткрывает квесты о гастрономии Баку и инвестициях в фермы.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конверсия квеста «Каспийская икра» в YAML, определены экономические и гастрономические крючки."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

