INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-delhi-2020-2029-sacred-cows', 'Дели 2020-2029 — «Священные коровы»', 'Игрок взаимодействует с коровами на дорогах, общается с жителями и выполняет подношения, формируя карму и социальный рейтинг.', 'side', 1, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "observe_street_cows", "text": "Анализировать перекрёсток с несколькими коровами, перенаправлять трафик, изучать плакаты и слушать объявление полиции о защите животных", "type": "observe", "target": "delhi_sacred_cows_observation", "count": 1, "optional": false}, {"id": "perform_devotion_rituals", "text": "Участвовать в мини-игре с кормлением коров, чтением мантр и получением благословения у храма", "type": "social", "target": "delhi_sacred_cows_devotion", "count": 1, "optional": false}, {"id": "support_community", "text": "Помочь волонтёрам организовать безопасный маршрут для стада, взаимодействовать с активистами и фиксировать истории для культурного архива", "type": "interact", "target": "delhi_sacred_cows_community", "count": 1, "optional": false}]'::jsonb, '{"experience": 600, "currency": 0, "items": [], "reputation": {"delhi_cultural": 10}, "flags": ["cow_friend_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-delhi-2020-2029-sacred-cows", "title": "Дели 2020-2029 — «Священные коровы»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["delhi", "quest", "hinduism"], "topics": ["religion", "social-norms"], "related_systems": ["narrative-service", "social-service", "culture-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/delhi/2020-2029/quest-007-sacred-cows.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Материал о священных коровах не предоставлял игровых механик для изучения религиозных норм и поведения NPC на улицах.", "goal": "Сконструировать культурный квест-наблюдение, в котором игрок учится уважать табу и участвует в ритуалах почитания.", "essence": "Игрок взаимодействует с коровами на дорогах, общается с жителями и выполняет подношения, формируя карму и социальный рейтинг.", "key_points": ["Индуистская концепция «Гау мата» определяет отношение к животным в городе.", "Транспорт уступает коровам, формируя уникальные правила трафика.", "Благотворительные ритуалы усиливают карму и открывают религиозные ветки."]}, "quest_definition": {"quest_type": "side", "level_min": 1, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "observe_street_cows", "text": "Анализировать перекрёсток с несколькими коровами, перенаправлять трафик, изучать плакаты и слушать объявление полиции о защите животных", "type": "observe", "target": "delhi_sacred_cows_observation", "count": 1, "optional": false}, {"id": "perform_devotion_rituals", "text": "Участвовать в мини-игре с кормлением коров, чтением мантр и получением благословения у храма", "type": "social", "target": "delhi_sacred_cows_devotion", "count": 1, "optional": false}, {"id": "support_community", "text": "Помочь волонтёрам организовать безопасный маршрут для стада, взаимодействовать с активистами и фиксировать истории для культурного архива", "type": "interact", "target": "delhi_sacred_cows_community", "count": 1, "optional": false}], "rewards": {"experience": 600, "currency": 0, "items": [], "reputation": {"delhi_cultural": 10}, "flags": ["cow_friend_achievement"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест окунает игрока в повседневную религиозную практику. Необходимо наблюдать, как коровы сосуществуют с транспортом,\\nи соблюсти нормы уважения, чтобы не потерять репутацию среди жителей и духовных лидеров.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "observation", "title": "Наблюдение на улицах", "body": "Игрок анализирует перекрёсток с несколькими коровами, перенаправляет трафик, изучает плакаты и слушает объявление\\nполиции о защите животных.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "devotion", "title": "Ритуалы почитания", "body": "Сцена у храма предлагает мини-игру с кормлением коров, чтением мантр и получением благословения. От выбора диалогов\\nзависит прирост кармы и социальная репутация.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "community_support", "title": "Поддержка сообщества", "body": "Игрок помогает волонтёрам организовать безопасный маршрут для стада, взаимодействует с активистами и фиксирует\\nистории для культурного архива.\\n", "mechanics_links": ["mechanics/world/community-reputation.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 600 XP, культура +10, финансовых затрат нет.\\n- Ачивка «Друг коров» и бонус к карме, влияющий на религиозные сюжетные линии.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Трекер уважения к культурным нормам.\\n- Мини-игры кормления и ухода за животными.\\n- Социальные проверки на эмпатию и знание терминологии индуизма.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест развивает религиозную линию Индии, ведёт к храмовым фестивалям и событиям кармических выборов, влияющих\\nна взаимодействие с духовными наставниками.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1024, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики религии и социальных норм."}, {"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Священные коровы» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

