INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-delhi-2020-2029-pollution-crisis', 'Дели 2020-2029 — «Кризис загрязнения»', 'Игрок готовится к сезону смога, выходит в Дели при критическом AQI, анализирует источники и принимает решения по защите сообщества.', 'main', 6, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "prepare_for_smog", "text": "Собрать данные о прогнозе AQI, подготовить маску N95 и фильтры для убежища", "type": "acquire", "target": "delhi_pollution_preparation", "count": 1, "optional": false}, {"id": "experience_smog_effects", "text": "Выйти на улицу и зафиксировать визуальные и физиологические эффекты смога", "type": "observe", "target": "delhi_pollution_smog_experience", "count": 1, "optional": false}, {"id": "investigate_pollution_sources", "text": "Исследовать источники загрязнения: сжигание стерни, транспортные артерии, промышленные зоны и праздники", "type": "research", "target": "delhi_pollution_sources", "count": 1, "optional": false}, {"id": "launch_activism_campaign", "text": "Опросить NPC, запустить кампанию подписей и договориться о временном режиме ограничения движения", "type": "social", "target": "delhi_pollution_activism", "count": 1, "optional": false}, {"id": "make_decision", "text": "Принять решение: эвакуация, инвестиции в очистители воздуха или участие в экологическом движении", "type": "interact", "target": "delhi_pollution_decision", "count": 1, "optional": false}]'::jsonb, '{"experience": 3000, "currency": -10, "items": [], "reputation": {"delhi_environmental": 15}, "flags": ["smog_survivor_achievement"]}'::jsonb, '[{"id": "evacuation_choice", "condition": "choice == \\"evacuation\\"", "rewards": {"reputation": {"delhi_survivors": 10}}}, {"id": "technology_choice", "condition": "choice == \\"technology\\"", "rewards": {"reputation": {"delhi_innovators": 15}}}, {"id": "activism_choice", "condition": "choice == \\"activism\\"", "rewards": {"reputation": {"delhi_environmental_activists": 20}}}]'::jsonb, '{"metadata": {"id": "canon-quest-delhi-2020-2029-pollution-crisis", "title": "Дели 2020-2029 — «Кризис загрязнения»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["delhi", "quest", "pollution"], "topics": ["environmental-crisis", "urban-health"], "related_systems": ["narrative-service", "social-service", "environment-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1026", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1026", "relation": "migrated_to", "migrated_at": "2025-11-23T02:25:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/delhi/2020-2029/quest-009-pollution-crisis.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экологическая линия Дели не раскрывала ежегодный смог при AQI 500+ и его влияние на жителей.", "goal": "Сформировать игровой сценарий переживания кризиса загрязнения с выбором стратегий защиты, эвакуации или активизма.", "essence": "Игрок готовится к сезону смога, выходит в Дели при критическом AQI, анализирует источники и принимает решения по защите сообщества.", "key_points": ["Осенний пик AQI превышает 500 из-за сжигания стерни, транспорта, заводов и фейерверков Дивали.", "Смог уменьшает видимость до 50 метров и вызывает кашель, жжение глаз и потерю выносливости.", "Квест даёт выбор между уходом из города, поддержкой экологических протестов и технологическими решениями очистки."]}, "quest_definition": {"quest_type": "main", "level_min": 6, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "prepare_for_smog", "text": "Собрать данные о прогнозе AQI, подготовить маску N95 и фильтры для убежища", "type": "acquire", "target": "delhi_pollution_preparation", "count": 1, "optional": false}, {"id": "experience_smog_effects", "text": "Выйти на улицу и зафиксировать визуальные и физиологические эффекты смога", "type": "observe", "target": "delhi_pollution_smog_experience", "count": 1, "optional": false}, {"id": "investigate_pollution_sources", "text": "Исследовать источники загрязнения: сжигание стерни, транспортные артерии, промышленные зоны и праздники", "type": "research", "target": "delhi_pollution_sources", "count": 1, "optional": false}, {"id": "launch_activism_campaign", "text": "Опросить NPC, запустить кампанию подписей и договориться о временном режиме ограничения движения", "type": "social", "target": "delhi_pollution_activism", "count": 1, "optional": false}, {"id": "make_decision", "text": "Принять решение: эвакуация, инвестиции в очистители воздуха или участие в экологическом движении", "type": "interact", "target": "delhi_pollution_decision", "count": 1, "optional": false}], "rewards": {"experience": 3000, "currency": -10, "items": [], "reputation": {"delhi_environmental": 15}, "flags": ["smog_survivor_achievement"]}, "branches": [{"id": "evacuation_choice", "condition": "choice == \\"evacuation\\"", "rewards": {"reputation": {"delhi_survivors": 10}}}, {"id": "technology_choice", "condition": "choice == \\"technology\\"", "rewards": {"reputation": {"delhi_innovators": 15}}}, {"id": "activism_choice", "condition": "choice == \\"activism\\"", "rewards": {"reputation": {"delhi_environmental_activists": 20}}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок переживает ноябрьский смог в Дели: получает предупреждение о критическом AQI, оснащается защитой и отслеживает динамику\\nзагрязнения, помогая жителям приспособиться к кризису.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Собрать данные о прогнозе AQI, подготовить маску N95 и фильтры для убежища.\\n2. Выйти на улицу и зафиксировать визуальные и физиологические эффекты смога.\\n3. Исследовать источники загрязнения: сжигание стерни, транспортные артерии, промышленные зоны и праздники.\\n4. Опросить NPC, запустить кампанию подписей и договориться о временном режиме ограничения движения.\\n5. Принять решение: эвакуация, инвестиции в очистители воздуха или участие в экологическом движении.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Ноябрьские ограничения видимости, запах дыма и раздражение глаз описывают атмосферу города. AQI выше 500 наносит вред всем\\nгруппам населения, включая людей с хорошим здоровьем.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 3 000 XP и атрибут «Выносливость» +15.\\n- Финансовые затраты 10 единиц валюты на средства защиты.\\n- Дебафф «Смог» (макс. HP -20%), пока игрок остаётся в Дели без стационарных очистителей.\\n- Ачивка «Выживший в смоге» и доступ к линиям экологического активизма.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Квест задействует экологические индикаторы, отслеживание AQI и социальные сценарии помощи. Решения игрока влияют на репутацию среди\\nзелёных фракций и состояние загрязнения в регионе.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "История связывает экологическую линию Дели с сюжетами о фермерских протестах, энергетической политике и климатических миссиях Южной Азии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1026, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики экологического кризиса и городского здоровья с ветвлениями."}, {"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Кризис загрязнения» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

