INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-delhi-2020-2029-partition-legacy', 'Дели 2020-2029 — «Наследие раздела»', 'Игрок исследует экспозиции, встречается с наследниками беженцев и формирует архив, влияющий на отношение фракций в регионе.', 'main', 8, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_partition_museum", "text": "Посетить Partition Museum, изучить карту миграций и хронику освобождения от Британии", "type": "explore", "target": "delhi_partition_museum", "count": 1, "optional": false}, {"id": "listen_witness_testimonies", "text": "Прослушать аудиозаписи очевидцев, рассмотреть личные вещи и фотографии семей", "type": "research", "target": "delhi_partition_testimonies", "count": 1, "optional": false}, {"id": "explore_conflict_areas", "text": "Исследовать районы, где происходили столкновения, и встретить NPC с травматичным опытом", "type": "investigate", "target": "delhi_partition_conflict_areas", "count": 1, "optional": false}, {"id": "visit_memorials", "text": "Посетить современные мемориалы и стены памяти, зафиксировать их состояние для отчёта", "type": "visit", "target": "delhi_partition_memorials", "count": 1, "optional": false}, {"id": "create_analytical_report", "text": "Составить аналитический отчёт о влиянии Раздела на нынешнюю геополитику и отношения общин", "type": "craft", "target": "delhi_partition_report", "count": 1, "optional": false}]'::jsonb, '{"experience": 5000, "currency": 0, "items": [], "reputation": {"delhi_historical": 25}, "flags": ["history_witness_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-delhi-2020-2029-partition-legacy", "title": "Дели 2020-2029 — «Наследие раздела»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["delhi", "quest", "partition"], "topics": ["historical-memory", "post-colonial-trauma"], "related_systems": ["narrative-service", "social-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}, {"id": "github-issue-1027", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1027", "relation": "migrated_to", "migrated_at": "2025-11-23T02:25:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/delhi/2020-2029/quest-010-partition-legacy.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Историческая линия Индии не раскрывала личные истории и последствия Раздела 1947 года для Дели.", "goal": "Погрузить игрока в Partition Museum и мемориальные локации, чтобы зафиксировать травму и её современный контекст.", "essence": "Игрок исследует экспозиции, встречается с наследниками беженцев и формирует архив, влияющий на отношение фракций в регионе.", "key_points": ["Раздел привёл к гибели до двух миллионов человек и миграции пятнадцати миллионов жителей.", "Дели стала центром насилия и логистическим узлом для беженцев.", "Тема вызывает политические споры, поэтому решения игрока отражаются на репутации среди общин."]}, "quest_definition": {"quest_type": "main", "level_min": 8, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "visit_partition_museum", "text": "Посетить Partition Museum, изучить карту миграций и хронику освобождения от Британии", "type": "explore", "target": "delhi_partition_museum", "count": 1, "optional": false}, {"id": "listen_witness_testimonies", "text": "Прослушать аудиозаписи очевидцев, рассмотреть личные вещи и фотографии семей", "type": "research", "target": "delhi_partition_testimonies", "count": 1, "optional": false}, {"id": "explore_conflict_areas", "text": "Исследовать районы, где происходили столкновения, и встретить NPC с травматичным опытом", "type": "investigate", "target": "delhi_partition_conflict_areas", "count": 1, "optional": false}, {"id": "visit_memorials", "text": "Посетить современные мемориалы и стены памяти, зафиксировать их состояние для отчёта", "type": "visit", "target": "delhi_partition_memorials", "count": 1, "optional": false}, {"id": "create_analytical_report", "text": "Составить аналитический отчёт о влиянии Раздела на нынешнюю геополитику и отношения общин", "type": "craft", "target": "delhi_partition_report", "count": 1, "optional": false}], "rewards": {"experience": 5000, "currency": 0, "items": [], "reputation": {"delhi_historical": 25}, "flags": ["history_witness_achievement"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест отправляет игрока в Partition Museum и по мемориальным маршрутам Дели. Нужно собрать артефакты, свидетельства и сформировать\\nрекомендации по сохранению памяти о трагедии 1947 года.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Посетить Partition Museum, изучить карту миграций и хронику освобождения от Британии.\\n2. Прослушать аудиозаписи очевидцев, рассмотреть личные вещи и фотографии семей.\\n3. Исследовать районы, где происходили столкновения, и встретить NPC с травматичным опытом.\\n4. Посетить современные мемориалы и стены памяти, зафиксировать их состояние для отчёта.\\n5. Составить аналитический отчёт о влиянии Раздела на нынешнюю геополитику и отношения общин.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Отмечены даты 15 августа 1947 года, массовые переселения и религиозное насилие. Дели демонстрирует способность поглощать волны беженцев,\\nно до сих пор хранит незаживающие раны.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 5 000 XP и эмоциональное состояние «Скорбь и понимание».\\n- Ачивка «Свидетель истории» и доступ к сюжетам примирения между общинами.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Используются образовательные, исторические и эмоциональные механики. Выбор игрока влияет на доверие культурных фракций и открывает\\nновые дипломатические миссии между Индией и Пакистаном.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает историческую линию Индии, связанные ветки независимости и сюжеты о постколониальной политике субконтинента.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1027, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики исторической памяти и постколониальной травмы."}, {"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Наследие раздела» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURREN