INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hong-kong-2020-2029-victoria-peak', 'Гонконг 2020-2029 — «Пик Виктория»', 'Игрок поднимается на Пик Виктория, исследует Sky Terrace 428 и фиксирует закат над Викторией Харбор.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hong-kong-2020-2029-victoria-peak", "title": "Гонконг 2020-2029 — «Пик Виктория»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["hong-kong", "quest", "victoria-peak"], "topics": ["urban-landscape", "tourism"], "related_systems": ["narrative-service", "exploration-service", "transport-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-001-victoria-peak.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В туристической линии Гонконга отсутствовал структурированный маршрут подъёма на Пик Виктория.", "goal": "Описать квест смотровой площадки с использованием исторического Peak Tram и панорамной выставки.", "essence": "Игрок поднимается на Пик Виктория, исследует Sky Terrace 428 и фиксирует закат над Викторией Харбор.", "key_points": ["Peak Tram обеспечивает исторический контекст и уникальный угол обзора.", "Смотровая площадка 428 даёт 360° панораму skyline.", "Квест усиливает эстетическую и туристическую линию Гонконга."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с культовым маршрутом на вершину Гонконга. Необходимо приобрести билет на Peak Tram,\\nподготовить фотооборудование и выбрать оптимальное время суток.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "ascent", "title": "Подъём на Peak Tram", "body": "Игрок проходит терминал Lower Peak Tram Terminus, изучает историю фуникулёра и поднимается по склону с уклоном до 27°.\\nВ пути открываются кат-сцены о развитии острова Гонконг.\\n", "mechanics_links": ["mechanics/transport/funicular-system.yaml"], "assets": []}, {"id": "sky_terrace", "title": "Sky Terrace 428", "body": "На вершине доступна смотровая площадка с аудиогидом. Игрок собирает метки панорам, отмечает здания и джонки в гавани,\\nа также активирует режим «Закат» для бонусов.\\n", "mechanics_links": ["mechanics/exploration/exploration-systems.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 800 XP, эстетика +15, финансовые затраты -50 HKD.\\n- Ачивка «На вершине Гонконга» и открытие фотозаданий skyline.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает туристическую линию Гонконга и связывается с заданиями гавани, Star Ferry и ночного лазерного шоу Symphony of Lights.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1011, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Пик Виктория» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

