INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-lavra-pilgrimage', 'Киев 2020-2029 — «Паломничество в Лавру»', 'Игрок проходит через Святые врата, исследует пещеры с мощами святых и получает защитный бафф.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-lavra-pilgrimage", "title": "Киев 2020-2029 — «Паломничество в Лавру»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T02:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["kiev", "lavra", "pilgrimage"], "topics": ["spirituality", "cultural-heritage"], "related_systems": ["narrative-service", "buff-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-kiev-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/kiev/2020-2029/quest-002-lavra-pilgrimage.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик квеста не описывал структуру паломничества и не связывал духовные действия с игровыми баффами.", "goal": "Оформить визит в Киево-Печерскую лавру с последовательностью обрядов, социальными взаимодействиями и благословением.", "essence": "Игрок проходит через Святые врата, исследует пещеры с мощами святых и получает защитный бафф.", "key_points": ["Историческая обитель 1051 года с золотыми куполами над Днепром.", "Лабиринт пещер и общение с монахами в чёрных рясах.", "Благословение даёт бонус к защите и открывает духовные ветки."]}, "content": {"sections": [{"id": "entrance", "title": "Вход в лавру", "body": "Игрок входит через Святые врата, слышит колокольный звон и наблюдает панораму золотых куполов. Настоятель объясняет правила\\nповедения и выдаёт карту маршрута.\\n", "mechanics_links": [], "assets": []}, {"id": "cathedral", "title": "Успенский собор", "body": "Посещение главного собора с реликвиями и стенописями. Игрок зажигает свечу, слушает службу и получает духовный отклик,\\nповышающий репутацию с религиозными фракциями.\\n", "mechanics_links": [], "assets": []}, {"id": "caves", "title": "Пещеры и мощи", "body": "Спуск в пещеры с мощами святых монахов. Игрок следует лабиринтом, отмечает святыни и ведёт запись ауры для timeline.\\nТемнота и запах ладана усиливают атмосферу.\\n", "mechanics_links": [], "assets": []}, {"id": "blessing", "title": "Благословение и награды", "body": "Финальный этап предполагает беседу с монахом и получение благословения. Игрок получает 1 000 XP, -10 едди (пожертвование),\\nбафф «Благословение» (+10 % к защите на 48 часов) и ачивку «Паломник Киева».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Киево-Печерская лавра", "definition": "Исторический православный монастырь, основанный в XI веке на берегу Днепра."}], "references": [{"title": "Kyiv Pechersk Lavra", "link": "https://whc.unesco.org/en/list/527/"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Паломничество в Лавру» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

