INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-buenos-aires-2029-010-economic-crisis', 'Буэнос-Айрес — экономический кризис', 'Игрок балансирует между официальным курсом песо и рынком blue dollar, поддерживая семьи и бизнес в условиях хронической нестабильности.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "gather_economic_info", "text": "Собрать информацию у экономистов и банков о текущей инфляции и контроле капитала", "type": "interact", "target": "economic_intelligence", "count": 1, "optional": false}, {"id": "choose_currency_strategy", "text": "Решить, использовать ли официальный курс или обращаться к рынку blue dollar", "type": "choice", "target": "currency_exchange_choice", "count": 1, "optional": false}, {"id": "support_family_business", "text": "Организовать поддержку семьи и малого бизнеса, сталкивающихся с ростом цен", "type": "interact", "target": "family_business_support", "count": 1, "optional": false}, {"id": "record_consequences", "text": "Зафиксировать последствия: рост доверия жителей или усиление теневой экономики", "type": "interact", "target": "crisis_consequences", "count": 1, "optional": false}]'::jsonb, '{"experience": 4000, "money": 0, "items": [], "reputation": {"economic": 30, "social": 25}, "unlocks": {"achievements": ["Выживший в кризисе"], "flags": []}}'::jsonb, '[{"condition": "Использовать официальный курс", "outcome": "Доверие государства, но финансовые потери", "next_quests": []}, {"condition": "Использовать blue dollar", "outcome": "Финансовая выгода, но усиление теневой экономики", "next_quests": []}]'::jsonb, '{"metadata": {"id": "quest-buenos-aires-2029-010-economic-crisis", "title": "Буэнос-Айрес — экономический кризис", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T11:10:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["buenos-aires", "economy", "crisis"], "topics": ["city_storylines", "socioeconomics"], "related_systems": ["narrative-service", "economy-service", "social-service"], "related_documents": [{"id": "quest-system", "relation": "references"}, {"id": "buenos-aires-economy-brief", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/buenos-aires/2020-2029/quest-010-economic-crisis.md", "visibility": "internal", "audience": ["concept", "narrative", "economy"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест про экономический кризис перечисляет факты без связи с игровыми системами и выбором игрока.", "goal": "Структурировать сценарий, позволяющий игроку пережить инфляцию, контролировать валюту и влиять на социальную стабильность.", "essence": "Игрок балансирует между официальным курсом песо и рынком blue dollar, поддерживая семьи и бизнес в условиях хронической нестабильности.", "key_points": ["Квест раскрывает механики инфляции 50%+, нестабильность песо и роль неформального рынка валюты.", {"Игрок выбирает стратегии выживания": "сбережения, контрабанда или поддержка государства."}, "Социальные последствия влияют на репутацию и доступ к экономическим событиям."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "gather_economic_info", "text": "Собрать информацию у экономистов и банков о текущей инфляции и контроле капитала", "type": "interact", "target": "economic_intelligence", "count": 1, "optional": false}, {"id": "choose_currency_strategy", "text": "Решить, использовать ли официальный курс или обращаться к рынку blue dollar", "type": "choice", "target": "currency_exchange_choice", "count": 1, "optional": false}, {"id": "support_family_business", "text": "Организовать поддержку семьи и малого бизнеса, сталкивающихся с ростом цен", "type": "interact", "target": "family_business_support", "count": 1, "optional": false}, {"id": "record_consequences", "text": "Зафиксировать последствия: рост доверия жителей или усиление теневой экономики", "type": "interact", "target": "crisis_consequences", "count": 1, "optional": false}], "rewards": {"experience": 4000, "money": 0, "items": [], "reputation": {"economic": 30, "social": 25}, "unlocks": {"achievements": ["Выживший в кризисе"], "flags": []}}, "branches": [{"condition": "Использовать официальный курс", "outcome": "Доверие государства, но финансовые потери", "next_quests": []}, {"condition": "Использовать blue dollar", "outcome": "Финансовая выгода, но усиление теневой экономики", "next_quests": []}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок погружается в аргентинскую экономику, где инфляция и девальвация стали нормой. Необходимо управлять ресурсами семьи, принимать\\nрешения о покупке долларов и поддерживать доверие жителей разных районов.\\n", "mechanics_links": ["mechanics/economy/economy-overview.yaml"], "assets": []}, {"id": "crisis_drivers", "title": "Региональные отсылки", "body": "Инфляция превышает 50% в год, песо постоянно теряет ценность, а black market («blue dollar») предлагает выгодный курс. Жители хранят\\nдоллары дома и регулярно переживают циклы кризисов: 2001, 2018, 2020 и далее.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Собрать информацию у экономистов и банков о текущей инфляции и контроле капитала.\\n2. Решить, использовать ли официальный курс или обращаться к рынку blue dollar.\\n3. Организовать поддержку семьи и малого бизнеса, сталкивающихся с ростом цен.\\n4. Зафиксировать последствия: рост доверия жителей или усиление теневой экономики.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест приносит 4 000 XP, прирост показателя «Экономика» на 30 единиц и ачивку «Выживший в кризисе». Материальных наград нет, но игрок\\nоткрывает экономические миссии и события, связанные с нестабильностью валюты.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "integration", "title": "Интеграции", "body": "economy-service моделирует инфляцию и курсы валют, social-service отслеживает настроение граждан, а narrative-service открывает события\\nантиправительственных протестов и реформ.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1202, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-11", "author": "concept_team", "changes": "Оформлен квест «Экономический кризис» в структурированном виде и описаны системные последствия."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

