INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hong-kong-2020-2029-kowloon-walled-city', 'Гонконг 2020-2029 — «Город-крепость Коулун»', 'Игрок проникает в лабиринт, выживает среди аномальной плотности застройки и раскрывает тайны центрального квартала.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hong-kong-2020-2029-kowloon-walled-city", "title": "Гонконг 2020-2029 — «Город-крепость Коулун»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["hong-kong", "quest", "kowloon-walled-city"], "topics": ["urban-labyrinths", "organized-crime"], "related_systems": ["narrative-service", "faction-service", "exploration-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-002-kowloon-walled-city.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В криминальной линии Гонконга отсутствовал квест о легендарном городе-крепости Коулун и его экстремальной плотности.", "goal": "Воссоздать экспедицию в реконструированный Kowloon Walled City с акцентом на вертикальную навигацию и конфликт с триадами.", "essence": "Игрок проникает в лабиринт, выживает среди аномальной плотности застройки и раскрывает тайны центрального квартала.", "key_points": ["Город-крепость демонстрирует экстремальную плотность 1,2 млн человек на км².", "Ритуалы триад и нелегальные клиники делают локацию lawless-зоной.", "Квест тренирует трёхмерную навигацию по коридорам и крышам."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест отправляет игрока в музей-реконструкцию Kowloon Walled City. Необходимо собрать снаряжение, отключить навигационные\\nпомощники и полагаться на карту, нарисованную жителями.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "ingress", "title": "Проникновение", "body": "Игрок использует скрытый вход, избегает камер и активирует режим скрытности. Первые этажи заняты нелегальными клиниками и мастерскими.\\n", "mechanics_links": ["mechanics/factions/territory-control.yaml"], "assets": []}, {"id": "vertical_maze", "title": "Вертикальный лабиринт", "body": "Центральная часть квеста — навигация по переходам, крышам и узким лестницам. Игрок использует трёхмерную карту, мониторит уровень света\\nи ищет короткие пути через квартиры.\\n", "mechanics_links": ["mechanics/exploration/vertical-navigation.yaml"], "assets": []}, {"id": "triad_encounter", "title": "Конфликт с триадами", "body": "Триады контролируют сердца кварталов. Игрок выбирает подход: подкупить лидера, вступить в бой или тайно пройти мимо, что влияет на репутацию.\\n", "mechanics_links": ["mechanics/factions/conflict-resolution.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 8 000 XP, редкие артефакты города-крепости, доступ к нелегальным контактам.\\n- Рост риска в отношении полиции и усиление наблюдения на границе.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест соединяет криминальные арки Гонконга с глобальными сюжетами о заброшенных мегаполисах и служит вступлением к триадным операциям.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1012, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Город-крепость Коулун» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

