INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-minsk-metro', 'Минск 2020-2029 — «Минское метро»', 'Игрок ездит по ключевым станциям, изучает мраморные залы и узнаёт о двойном назначении метро.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-minsk-metro", "title": "Минск 2020-2029 — «Минское метро»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T03:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["minsk", "metro", "architecture"], "topics": ["exploration", "infrastructure"], "related_systems": ["narrative-service", "transport-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-minsk-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/minsk/2020-2029/quest-009-minsk-metro.md", "visibility": "internal", "audience": ["lore", "narrative", "tourism"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик не описывал тур по станциям минского метро и его связь с городскими системами.", "goal": "Создать экскурсионный квест по архитектуре и инфраструктуре Минска.", "essence": "Игрок ездит по ключевым станциям, изучает мраморные залы и узнаёт о двойном назначении метро.", "key_points": ["Станции «Площадь Ленина», «Октябрьская» и «Площадь Победы».", "Чистота, советские мозаики, глубина и функция бомбоубежища.", "Награды повышают культуру и транспортные знания."]}, "content": {"sections": [{"id": "tour", "title": "Экскурсия по станциям", "body": "Гид показывает мрамор, люстры и мозаики ключевых станций. Игрок фотографирует детали для культурного архива.\\n", "mechanics_links": [], "assets": []}, {"id": "operations", "title": "Эксплуатация и глубина", "body": "Посещение технических зон, обсуждение чистоты и роли метро как бомбоубежища. Игрок получает доступ к транспортным данным.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "1 000 XP, -1 едди (жетон), +10 к параметру «Культура» и ачивка «Метрополитеновец».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Мінскі метрапалітэн", "definition": "Метрополитен Минска, открытый в 1984 году и известный своей чистотой."}], "references": [{"title": "Minsk Metro Official Site", "link": "https://metrominsk.by/"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Минское метро» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

