INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hong-kong-2020-2029-star-ferry', 'Гонконг 2020-2029 — «Паром Звёздный»', 'Игрок пересекает гавань на Star Ferry, собирая панорамные заметки и взаимодействуя с пассажирами.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hong-kong-2020-2029-star-ferry", "title": "Гонконг 2020-2029 — «Паром Звёздный»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["hong-kong", "quest", "star-ferry"], "topics": ["transport-history", "urban-tourism"], "related_systems": ["narrative-service", "transport-service", "liveops-events-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-003-star-ferry.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Транспортная линия Гонконга не описывала культовую переправу Star Ferry как самостоятельный квест.", "goal": "Закрепить короткий маршрут через Виктория Харбор с акцентом на виды и историческое наследие 1888 года.", "essence": "Игрок пересекает гавань на Star Ferry, собирая панорамные заметки и взаимодействуя с пассажирами.", "key_points": ["Маршрут Central ↔ Tsim Sha Tsui демонстрирует skyline сразу двух берегов.", "Низкая стоимость билета делает квест доступным на ранних этапах.", "Событие поддерживает атмосферу живого порта с джонками и контейнеровозами."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест протекает в реальном времени — 10 минут пересечения Виктория Харбор. Игрок покупает бумажный билет, сканирует его на турникете\\nи наблюдает за посадкой местных жителей.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "ferry_crossing", "title": "Путешествие через гавань", "body": "Во время рейса игрок может активировать фотозадания Skyline Focus, слушать аудиогиды и отмечать проходящие суда. От выбора позиции зависит качество видов\\nи вероятность встретить NPC-туристов.\\n", "mechanics_links": ["mechanics/transport/ferry-system.yaml"], "assets": []}, {"id": "arrival", "title": "Прибытие в Tsim Sha Tsui", "body": "По прибытию игрок получает доступ к набережной, музею космоса и вечернему шоу Symphony of Lights. Возможны побочные взаимодействия с уличными музыкантами.\\n", "mechanics_links": ["mechanics/liveops/event-hooks.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 500 XP, ностальгия +10, финансовые затраты -3 HKD.\\n- Ачивка «Пассажир Star Ferry» и открытие цепочки водных маршрутов.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест усиливает транспортную линию Гонконга и служит переходом к событиям на набережной, включая паркур по крышам и гастрономическую улицу Temple Street.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1013, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Паром Звёздный» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

