-- Quest: canon-quest-hong-kong-2020-2029-dim-sum-masters
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hong-kong-2020-2029-dim-sum-masters', 'Гонконг 2020-2029 — «Мастера дим-сама»', 'Игрок собирает отзывы местных жителей, посещает три заведения разного уровня, дегустирует разнообразные дим-самы и выбирает победителя.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hong-kong-2020-2029-dim-sum-masters", "title": "Гонконг 2020-2029 — «Мастера дим-сама»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["hong-kong", "quest", "dim-sum"], "topics": ["culinary-culture", "urban-exploration"], "related_systems": ["narrative-service", "social-service", "food-and-beverage-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-004-dim-sum-masters.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Дорожная карта квеста о лучших дим-самах Гонконга отсутствовала в базе знаний и не описывала геймплейный тур дегустаций.", "goal": "Стандартизировать гастрономический маршрут по ключевым чайным домам с оценкой блюд, сервиса и культурных особенностей.", "essence": "Игрок собирает отзывы местных жителей, посещает три заведения разного уровня, дегустирует разнообразные дим-самы и выбирает победителя.", "key_points": ["Tim Ho Wan, Maxim''s Palace и Lung King Heen обеспечивают спектр от народного Michelin до элитной кухни.", "Культура yum cha включает чайный этикет, шум и общение с тележками.", "Итоговый рейтинг открывает дополнительные гастрономические цепочки Гонконга."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест погружает игрока в столичную сцену дим-сама. Нужно опросить местных жителей, получить рекомендации,\\nсоставить план посещений и освоить этикет чайного дома.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "preparation", "title": "Подготовка и опросы", "body": "Игрок исследует районы Central и Sham Shui Po, собирая мнения о лучших тележках и мишленовских заведениях.\\nСоциальные навыки определяют доступ к закрытым залам и скрытым блюдам.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "tasting_route", "title": "Маршрут дегустации", "body": "1. Tim Ho Wan — бюджетный Michelin с акцентом на быстрое обслуживание и выпечку char siu bao.\\n2. Maxim''s Palace — традиционный банкетный зал с тележками и шумной атмосферой.\\n3. Lung King Heen — ресторан с 3 звёздами Michelin и инновационной подачей кантонской кухни.\\nИгрок анализирует har gow, siu mai, char siu bao и чайные пары.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "scoring", "title": "Оценка и вывод", "body": "После дегустации игрок заполняет таблицу критериев (вкус, сервис, атмосфера, цена), выбирает победителя и публикует\\nрекомендательный отчёт, влияющий на экономику модуля питания.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 000 XP, бафф «Сытость» (+30% HP на 12 часов), финансовые затраты -200 HKD.\\n- Ачивка «Dim Sum Master» и доступ к гастрономическим событиям yum cha.\\n", "mechanics_links": ["mechanics/progression/progression-buffs.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает кулинарную линию Гонконга, связывает её с сюжетами о Michelin-охоте и глобальной кантонской кухне.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1014, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Мастера дим-сама» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

