-- Quest: canon-quest-hong-kong-2020-2029-triad-initiation
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hong-kong-2020-2029-triad-initiation', 'Гонконг 2020-2029 — «Инициация в триаду»', 'Игрок устанавливает контакт, выполняет испытания, проходит церемонию 36 клятв и получает статус «49».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hong-kong-2020-2029-triad-initiation", "title": "Гонконг 2020-2029 — «Инициация в триаду»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["hong-kong", "quest", "triads"], "topics": ["organized-crime", "faction-progression"], "related_systems": ["narrative-service", "faction-service", "reputation-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-005-triad-initiation.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Фракционные сюжеты Гонконга не раскрывали процесс вступления в триаду и последствия кровавой клятвы.", "goal": "Создать многоступенчатый квест, демонстрирующий экономику, ритуалы и необратимость присоединения к триаде.", "essence": "Игрок устанавливает контакт, выполняет испытания, проходит церемонию 36 клятв и получает статус «49».", "key_points": ["Испытание в Монг Коке затрагивает механики сбора дани и конфликты с конкурентами.", "Церемония 36 клятв и кровавый ритуал с благовониями подчеркивают культурный код триады.", "После инициации открываются эксклюзивные контракты, но выход из фракции невозможен."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест описывает путь игрока от первого контакта до церемонии вступления в одну из гонконгских триад. Требуется балансировать\\nмежду насилием, уважением к традициям и скрытностью.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "recruitment", "title": "Вербовка и испытание", "body": "Игрок выполняет поручение на территории Монг Кока: собирает дань, уклоняется от полиции и демонстрирует лояльность.\\nВетка отражает экономику триады и угрозу конкурентных банд.\\n", "mechanics_links": ["mechanics/factions/territory-control.yaml"], "assets": []}, {"id": "initiation_ritual", "title": "Церемония 36 клятв", "body": "Ритуал проходит у алтаря Гуань Ди: чтение клятв, подношения, использование благовоний и совместное распитие крови.\\nИгрок выбирает формулировки клятв и получает татуировку дракона.\\n", "mechanics_links": ["mechanics/social/oath-system.yaml"], "assets": []}, {"id": "aftermath", "title": "Последствия и статус", "body": "После церемонии игрок получает статус «49», доступ к триадным контрактам, защиту и штраф за попытку предательства.\\nВетка интегрируется с глобальной сетью криминальных квестов.\\n", "mechanics_links": ["mechanics/reputation/criminal-reputation.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связан с триадными событиями Нью-Йорка и Сан-Франциско, а также с глобальной линейкой китайских криминальных синдикатов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1015, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Инициация в триаду» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

