INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hong-kong-2020-2029-sampan-ride', 'Гонконг 2020-2029 — «Поездка на сампане»', 'Игрок нанимает сампан, исследует плавучую деревню, посещает Jumbo Kingdom и знакомится с бытом семей лодочников.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hong-kong-2020-2029-sampan-ride", "title": "Гонконг 2020-2029 — «Поездка на сампане»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["hong-kong", "quest", "maritime"], "topics": ["cultural-tourism", "heritage"], "related_systems": ["narrative-service", "exploration-service", "food-and-beverage-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-007-sampan-ride.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Традиционная линия Гонконга не отображала плавучую деревню Абердина и опыт поездки на сампане.", "goal": "Сформировать атмосферный тур, подчёркивающий морское наследие и взаимодействие с местными жителями.", "essence": "Игрок нанимает сампан, исследует плавучую деревню, посещает Jumbo Kingdom и знакомится с бытом семей лодочников.", "key_points": ["Сампаны управляются пожилыми жителями, передающими лодки поколениями.", "Jumbo Kingdom выступает кульминацией гастрономического этапа с морепродуктами.", "Квест фиксирует исчезающую культуру Абердина."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест фокусируется на традиционной жизни в гавани Абердин. Игрок изучает историю сампанов, особенности проживания\\nна лодках и взаимодействует с местными семьями.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "hire", "title": "Наём сампана", "body": "Игрок договаривается с капитаном, выбирает продолжительность тура, узнаёт о суевериях и получает базовые инструкции\\nпо безопасности. Правильные диалоги повышают доверие.\\n", "mechanics_links": ["mechanics/social/player-orders-creation.yaml"], "assets": []}, {"id": "tour", "title": "Тур по плавучей деревне", "body": "Маршрут охватывает дома-лодки, рыбацкие джонки и легендарный ресторан Jumbo Kingdom. Игрок собирает аудиоистории,\\nпомогает с хозяйственными делами и документирует исчезающую культуру.\\n", "mechanics_links": ["mechanics/exploration/exploration-systems.yaml"], "assets": []}, {"id": "seafood", "title": "Морепродукты и финал", "body": "В заключение игрок дегустирует блюда на Jumbo Kingdom, открывает баффы от морепродуктов и возвращается на берег,\\nполучая благодарность жителей.\\n", "mechanics_links": ["mechanics/food/seafood-effects.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 800 XP, культура +10, финансовые затраты -100 HKD.\\n- Ачивка «Капитан сампана» и доступ к морским экскурсиям региона.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест усиливает традиционную линию Гонконга, связывая её с морскими событиями, рыбацкими сюжетами и кулинарными фестивалями.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1017, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Поездка на сампане» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

