INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-hong-kong-2020-2029-mong-kok-density', 'Гонконг 2020-2029 — «Плотность Монг Кока»', 'Игрок пробирается через Монг Кок, посещает рынки, выполняет задания среди толп и уходит из района без потерь.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-hong-kong-2020-2029-mong-kok-density", "title": "Гонконг 2020-2029 — «Плотность Монг Кока»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["hong-kong", "quest", "crowd"], "topics": ["urban-density", "market-culture"], "related_systems": ["narrative-service", "mobility-service", "social-service"], "related_documents": [{"id": "github-issue-1008", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1008", "relation": "migrated_to", "migrated_at": "2025-11-23T02:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/hong-kong/2020-2029/quest-008-mong-kok-density.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Маршрут по сверхплотному району Монг Кок не описывал навигацию в толпе и рыночные испытания.", "goal": "Создать квест выживания в час пик с акцентом на рынки, стресс-менеджмент и эвакуацию.", "essence": "Игрок пробирается через Монг Кок, посещает рынки, выполняет задания среди толп и уходит из района без потерь.", "key_points": ["Плотность населения до 130 000 человек/км² создаёт постоянное давление.", "Рынки Ladies'', Goldfish и Bird предлагают уникальные задания и торг.", "Квест тренирует управление стрессом, дыханием и построение маршрутов."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест бросает игрока в самую оживлённую часть Гонконга. Нужно распланировать маршрут, следить за стрессом и успеть выполнить\\nсписок задач до закрытия рынков.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "crowd_control", "title": "Навигация в толпе", "body": "Игрок использует HUD плотности, карманы для отдыха и технику дыхания. Неправильные действия приводят к штрафам и микротравмам.\\n", "mechanics_links": ["mechanics/mobility/crowd-navigation.yaml"], "assets": []}, {"id": "market_tasks", "title": "Задания на рынках", "body": "Нужно посетить Ladies'' Market, Goldfish Market и Bird Garden, приобрести три уникальных товара и задокументировать их для коллекции.\\nТорг повышает прибыль и репутацию.\\n", "mechanics_links": ["mechanics/economy/market-barter.yaml"], "assets": []}, {"id": "evacuation", "title": "Эвакуация", "body": "Завершив покупки, игрок должен покинуть район во время внезапного дождя, ориентируясь по неоновым вывескам и пешеходным мостам.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 2 000 XP, выносливость +15, финансовые затраты -500 HKD.\\n- Ачивка «Король плотности» и доступ к экстремальным навигационным испытаниям мегаполисов.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает урбанистическую линию Гонконга и связывается с сюжетами управления толпами в других мегаполисах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1008, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Плотность Монг Кока» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

