INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-khreschatyk-walk', 'Киев 2020-2029 — «Прогулка по Крещатику»', 'Игрок гуляет по пешеходному Крещатику, общается с киевлянами и снимает стресс.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-khreschatyk-walk", "title": "Киев 2020-2029 — «Прогулка по Крещатику»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T02:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["kiev", "khreschatyk", "lifestyle"], "topics": ["social", "tourism"], "related_systems": ["narrative-service", "mood-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-kiev-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/kiev/2020-2029/quest-009-khreschatyk-walk.md", "visibility": "internal", "audience": ["lore", "narrative", "live-ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик прогулки не описывал события выходного дня и влияние на социальные связи игрока.", "goal": "Зафиксировать сценарий «Киевского Бродвея» с музыкантами, кафе и расслаблением.", "essence": "Игрок гуляет по пешеходному Крещатику, общается с киевлянами и снимает стресс.", "key_points": ["Закрытие улицы по выходным и традиция каштанов.", "Уличные артисты, кафе и встречи с жителями.", "Награды снижают стресс и дают ачивку."]}, "content": {"sections": [{"id": "start", "title": "Начало прогулки", "body": "Игрок приходит на Крещатик в выходной день, когда движение закрыто, и улица наполняется пешеходами и музыкантами.\\n", "mechanics_links": [], "assets": []}, {"id": "activities", "title": "Атмосфера и активности", "body": "Переход от Майдана к Европейской площади. Встречи с художниками, фонтанами и кафе под каштанами, возможность взаимодействия\\nс NPC и игроками.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "500 XP, -500 едди (кафе), -30 % накопленного стресса и ачивка «Прогулка Киевлянина». Репутация в туристической линии растёт.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Крещатик", "definition": "Центральная улица Киева длиной около 1,2 км, перекрываемая для пешеходов по выходным."}], "references": [{"title": "Khreschatyk Street Guide", "link": "https://visitkyiv.travel/ru/places/khreshchatyk"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Прогулка по Крещатику» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

