INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-borsch-quest', 'Киев 2020-2029 — «Квест борща»', 'Игрок собирает отзывы, дегустирует три вида борща с салом и пампушками и объявляет победителя.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-borsch-quest", "title": "Киев 2020-2029 — «Квест борща»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T02:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["kiev", "cuisine", "borsch"], "topics": ["culinary", "cultural-identity"], "related_systems": ["narrative-service", "buff-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-kiev-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/kiev/2020-2029/quest-004-borsch-quest.md", "visibility": "internal", "audience": ["lore", "narrative", "live-ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик не показывал разнообразие борща и репутационные эффекты гастрономических предпочтений.", "goal": "Оформить дегустационный маршрут по киевским ресторанам с голосованием и культурными спорами.", "essence": "Игрок собирает отзывы, дегустирует три вида борща с салом и пампушками и объявляет победителя.", "key_points": ["Красный, зелёный и холодный борщ как отдельные станции.", "Социальные споры о происхождении блюда и культурная идентичность.", "Бафф на здоровье и доступ к кулинарным событиям."]}, "content": {"sections": [{"id": "survey", "title": "Сбор рекомендаций", "body": "Игрок опрашивает жителей Подола, Печерска и Оболони о лучшем борще. Формируется список ресторанов и визуализируется карта маршрута.\\n", "mechanics_links": [], "assets": []}, {"id": "tasting", "title": "Дегустация", "body": "Посещение трёх точек с разными стилями: классический красный борщ со свеклой, зелёный со щавелем и холодный летний вариант.\\nОбязательные дополнения: сметана, сало и пампушки.\\n", "mechanics_links": [], "assets": []}, {"id": "debate", "title": "Финал и спор", "body": "Игрок объявляет победителя, запускает спор с приезжими из Москвы и фиксирует культурный эффект.\\nВозможность выбрать дипломатичный, провокационный или шутливый итог.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "600 XP, -30 едди и бафф «Сытость» (+25 % HP на 8 часов). Ачивка «Знаток борща» и рост репутации в кулинарной линии Киева.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Пампушки", "definition": "Традиционные украинские чесночные булочки, подаваемые к борщу."}], "references": [{"title": "Ukrainian Borscht UNESCO Listing", "link": "https://ich.unesco.org/en/USL/culture-of-ukrainian-borscht-cooking-01722"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Квест борща» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

