-- Quest: quest-001-willis-tower
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-001-willis-tower', 'Чикаго — Башня Уиллис (The Ledge)', 'Игрок поднимается на смотровую площадку Willis Tower, испытывает стеклянный балкон The Ledge и открывает архитектурную линейку Чикаго.', 'side', 8, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "reach_skydeck", "text": "Подняться на Skydeck (103 этаж) и пройти инструктаж", "type": "travel", "target": "willis_tower_skydeck", "count": 1, "optional": false}, {"id": "experience_ledge", "text": "Выйти на стеклянный балкон The Ledge, наблюдая 412 метров вниз", "type": "interact", "target": "the_ledge_balcony", "count": 1, "optional": false}, {"id": "take_panoramic_photos", "text": "Сделать панорамные снимки и зафиксировать эмоцию «Парящий над городом»", "type": "interact", "target": "photo_capture", "count": 1, "optional": false}, {"id": "get_history_card", "text": "Получить карточку с историей переименования башни и доступ к архитектурной линии Чикаго", "type": "interact", "target": "history_exhibit", "count": 1, "optional": false}]'::jsonb, '{"experience": 1200, "money": -30, "reputation": {"adrenaline": 20}, "items": [], "unlocks": {"achievements": ["Повелитель Высот Чикаго"], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-001-willis-tower", "title": "Чикаго — Башня Уиллис (The Ledge)", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["chicago", "skyline", "tourism"], "topics": ["timeline_author", "regional_quests"], "related_systems": ["quest-engine", "tourism-service", "social-service"], "related_documents": [{"id": "questline-chicago-architecture", "relation": "collection"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/quest-001-willis-tower.md", "visibility": "internal", "audience": ["concept", "narrative", "events"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристический квест «Башня Уиллис» описан в свободной форме и не вписывается в набор региональных знаний Чикаго.", "goal": "Перенести ключевые элементы опыта The Ledge в структурированное описание, пригодное для хронологии 2020–2029.", "essence": "Игрок поднимается на смотровую площадку Willis Tower, испытывает стеклянный балкон The Ledge и открывает архитектурную линейку Чикаго.", "key_points": ["Визит на Skydeck и стеклянный балкон The Ledge с высотой 412 метров.", "Квест подчёркивает историческое значение башни и её переименование.", "Связан с архитектурной веткой и городскими баффами смелости/адреналина."]}, "quest_definition": {"quest_type": "side", "level_min": 8, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "reach_skydeck", "text": "Подняться на Skydeck (103 этаж) и пройти инструктаж", "type": "travel", "target": "willis_tower_skydeck", "count": 1, "optional": false}, {"id": "experience_ledge", "text": "Выйти на стеклянный балкон The Ledge, наблюдая 412 метров вниз", "type": "interact", "target": "the_ledge_balcony", "count": 1, "optional": false}, {"id": "take_panoramic_photos", "text": "Сделать панорамные снимки и зафиксировать эмоцию «Парящий над городом»", "type": "interact", "target": "photo_capture", "count": 1, "optional": false}, {"id": "get_history_card", "text": "Получить карточку с историей переименования башни и доступ к архитектурной линии Чикаго", "type": "interact", "target": "history_exhibit", "count": 1, "optional": false}], "rewards": {"experience": 1200, "money": -30, "reputation": {"adrenaline": 20}, "items": [], "unlocks": {"achievements": ["Повелитель Высот Чикаго"], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "introduction", "title": "Введение", "body": "Willis Tower (ранее Sears Tower) — культовый небоскрёб Чикаго высотой 442 метра. Смотровая площадка Skydeck и стеклянный балкон The Ledge формируют основу туристического квеста, где игрок испытывает высоту и открывает архитектурные факты города.\\n", "mechanics_links": ["mechanics/social/tourism-system.yaml"], "assets": []}, {"id": "regional_context", "title": "Региональные отсылки", "body": "- Башня удерживала титул самой высокой в мире 25 лет.\\n- Местные жители до сих пор называют её Sears Tower.\\n- The Ledge — стеклянные короба, выступающие на 1.3 метра.\\n- Панорама включает даунтаун и озеро Мичиган.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/chicago-overview.yaml"], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Подняться на Skydeck (103 этаж) и пройти инструктаж.\\n2. Выйти на стеклянный балкон The Ledge, наблюдая 412 метров вниз.\\n3. Сделать панорамные снимки и зафиксировать эмоцию «Парящий над городом».\\n4. Получить карточку с историей переименования башни и доступ к архитектурной линии Чикаго.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "- Базовые награды: 1 200 XP, −30 локальной валюты, адреналин +20.\\n- Ачивка «Повелитель Высот Чикаго».\\n- Разблокировка архитектурной цепочки и городского баффа смелости.\\n", "mechanics_links": ["mechanics/progression/rewards-catalog.yaml"], "assets": []}, {"id": "immersion", "title": "Атмосфера и связи", "body": "Зеркальные поверхности, рассказы гидов о Sears Tower и возможность увидеть город с высоты делают квест обязательным для первой посещаемости. Связан с региональной хронологией 2020–2029 и служит отправной точкой для архитектурных и культурных маршрутов.\\n", "mechanics_links": ["canon/lore/_03-lore/timeline-author/quests/america/chicago/2020-2029/index.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1183, "needs_task": false, "queue_reference": ["shared/trackers/queues/vision/review.yaml#quest-chicago-2029-001"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-09", "author": "concept_director", "changes": "Перенос туристического квеста башни Уиллис в структурированный формат."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлен quest_definition с полной структурой квеста согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

