INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-2020-2029-pc-bang-champion', 'Сеул 2020-2029 — «Чемпион PC Bang»', 'Игрок приходит в PC Bang, выбирает игру и проходит турнир до финала, становясь легендой заведения.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-2020-2029-pc-bang-champion", "title": "Сеул 2020-2029 — «Чемпион PC Bang»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:10:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["seoul", "quest", "esports"], "topics": ["competitive-play", "gaming-culture"], "related_systems": ["narrative-service", "esports-service"], "related_documents": [{"id": "github-issue-999", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/999", "relation": "migrated_to", "migrated_at": "2025-11-23T02:10:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/seoul/2020-2029/quest-002-pc-bang-champion.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Киберспортивная линия Сеула не показывала атмосферу PC Bang и эскалацию локального турнира.", "goal": "Добавить интерактивный турнир в корейском игровом кафе с выбором дисциплины и многокруговой сеткой.", "essence": "Игрок приходит в PC Bang, выбирает игру и проходит турнир до финала, становясь легендой заведения.", "key_points": ["PC Bang функционирует 24/7 и обслуживает сотни игроков сразу.", "Выбор дисциплины (StarCraft II или LoL) влияет на мини-игры и комментаторов.", "Победа приносит титул и эксклюзив киберспортивные контакты."]}, "content": {"sections": [{"id": "preparation", "title": "Подготовка к турниру", "body": "Игрок оплачивает время, настраивает клавиатуру и знакомится с соперниками. Вводятся баффы от рамена и энергетиков, влияющие\\nна концентрацию.\\n", "mechanics_links": ["mechanics/economy/pc-bang-services.yaml"], "assets": []}, {"id": "bracket", "title": "Турнирная сетка", "body": "Турнир проходит в формате групп + плей-офф. Игрок выбирает дисциплину и участвует в мини-играх, имитирующих ключевые моменты матча.\\n", "mechanics_links": ["mechanics/esports/tournament-structure.yaml"], "assets": []}, {"id": "finals", "title": "Финал и признание", "body": "Финальный матч проходит на сцене PC Bang со стримом. В зависимости от результатов игрок получает титул «PC Bang Legend» или\\nдругие призы.\\n", "mechanics_links": ["mechanics/media/live-stream-events.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- Победа: 30 000 еддис, титул «PC Bang Legend», +20 к киберспорту.\\n- Участие: 2 000 XP и уважение среди игроков.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает киберспортивную линию Сеула с глобальными турнирами и будущими LiveOps-ивентами (например, мировым финалом).\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 999, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Чемпион PC Bang» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

