INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-kiev-motherland-monument', 'Киев 2020-2029 — «Родина-мать»', 'Игрок исследует музей, поднимается по внутренним конструкциям статуи и наблюдает Киев с высоты 91 метра.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-kiev-motherland-monument", "title": "Киев 2020-2029 — «Родина-мать»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T02:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["kiev", "monument", "ww2-memory"], "topics": ["exploration", "cultural-heritage"], "related_systems": ["narrative-service", "exploration-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-kiev-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/kiev/2020-2029/quest-005-motherland-monument.md", "visibility": "internal", "audience": ["lore", "narrative", "tourism"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик не раскрывал масштаб восхождения внутрь статуи и связь с музейным комплексом ВОВ.", "goal": "Структурировать квест по посещению монумента «Родина-мать» с последовательными этапами и панорамной кульминацией.", "essence": "Игрок исследует музей, поднимается по внутренним конструкциям статуи и наблюдает Киев с высоты 91 метра.", "key_points": ["Титановая конструкция и символика меча и щита.", "Требование разрешения и физические испытания подъёма.", "Патриотический эффект и сравнение с другими мегастатуями мира."]}, "content": {"sections": [{"id": "museum", "title": "Музейный комплекс", "body": "Квест начинается в музее истории Второй мировой войны у подножия. Игрок изучает экспозиции, встречает гида и получает доступ\\nк экскурсии внутрь статуи.\\n", "mechanics_links": [], "assets": []}, {"id": "ascent", "title": "Подъём внутри статуи", "body": "Игрок проходит узкими лестницами и техническими шахтами, преодолевая сотни ступеней. Контроль стрессоустойчивости и физической\\nформы определяет скорость подъёма.\\n", "mechanics_links": [], "assets": []}, {"id": "observation", "title": "Смотровая площадка", "body": "На высоте 91 метр открывается панорама Днепра и Киева. Игрок активирует голографический гид по достопримечательностям,\\nсравнивает монумент с мировыми аналогами и фиксирует данные в timeline.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "2 500 XP, +10 к параметру «Патриотизм» и ачивка «На плече Родины-матери». Событие открывает цепочку «Гигантские статуи».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "«Родина-мать» (Киев)", "definition": "Монументальный памятник советского периода высотой 102 метра, посвящённый защите Родины."}], "references": [{"title": "Motherland Monument Kyiv", "link": "https://www.worldhistory.org/Kyiv_Motherland_Monument/"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Родина-мать» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, conte