-- Quest: canon-quest-seoul-2020-2029-han-river-festival
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-2020-2029-han-river-festival', 'Сеул 2020-2029 — «Фестиваль реки Хан»', 'Игрок арендует велосипед, объезжает парк Yeouido Hangang, проводит пикник и завершает день шоу фонтанов и салютом.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-2020-2029-han-river-festival", "title": "Сеул 2020-2029 — «Фестиваль реки Хан»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["seoul", "quest", "han-river"], "topics": ["urban-leisure", "cultural-festivals"], "related_systems": ["narrative-service", "exploration-service", "liveops-events-service"], "related_documents": [{"id": "github-issue-1004", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1004", "relation": "migrated_to", "migrated_at": "2025-11-23T02:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/seoul/2020-2029/quest-007-han-river-festival.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линия досуга Сеула не описывала фестивальный опыт на реке Хан с веломаршрутом и пикником.", "goal": "Сформировать квест отдыха в парках Hangang с акцентом на велопрогулку, chimaek и вечерний фейерверк.", "essence": "Игрок арендует велосипед, объезжает парк Yeouido Hangang, проводит пикник и завершает день шоу фонтанов и салютом.", "key_points": ["Парк Yeouido Hangang служит центральной площадкой фестиваля и велоактивностей.", "Комбинация chimaek и уличных шоу отражает корейский стиль вечерних пикников.", "Фейерверки и фонтанные шоу завершают цепочку событий и влияют на настроение персонажа."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит игрока на набережную реки Хан в разгар фестиваля. Необходимо подготовить пикниковый набор, выбрать маршрут и синхронизироваться\\nс расписанием мероприятий на сцене.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "bike_route", "title": "Веломаршрут вдоль Хан", "body": "Игрок арендует велосипед в Yeouido Hangang, исследует 40-километровые дорожки и собирает обзорные точки мостов. Дополнительные задания связаны с тайм-триалами\\nи взаимодействием с NPC-спортсменами.\\n", "mechanics_links": ["mechanics/mobility/bike-system.yaml"], "assets": []}, {"id": "picnic", "title": "Пикник и chimaek", "body": "После заезда игрок проводит пикник, заказывает курицу и пиво через мобильные сервисы, собирает бонусы настроения и слушает живую музыку. Можно разблокировать\\nсоциальные сцены с местными жителями.\\n", "mechanics_links": ["mechanics/social/social-gatherings.yaml"], "assets": []}, {"id": "finale", "title": "Вечерний финал", "body": "Финальная фаза включает шоу фонтанов Banpo Bridge и фейерверк. Игрок выбирает точку обзора, записывает видео и получает эстетические очки.\\n", "mechanics_links": ["mechanics/liveops/event-hooks.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 600 XP, снижение стресса -50%, финансовые затраты -30 KRW.\\n- Ачивка «Пикник на Хан» и открытие сезонных фестивалей Hangang.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест соединяет туристическую линию Сеула с событиями выхода на воду, муз-фестивалями и велопробегами, влияя на расписание liveops.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1004, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Фестиваль реки Хан» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

