-- Quest: canon-quest-seoul-2020-2029-taekwondo-dojo
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-2020-2029-taekwondo-dojo', 'Сеул 2020-2029 — «Додзё тхэквондо»', 'Игрок вступает в додзё, проходит интенсивную программу и получает мастерский титул, влияющий на рукопашный бой.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-2020-2029-taekwondo-dojo", "title": "Сеул 2020-2029 — «Додзё тхэквондо»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["seoul", "quest", "taekwondo"], "topics": ["martial-arts", "character-progression"], "related_systems": ["narrative-service", "combat-service", "training-service"], "related_documents": [{"id": "github-issue-1005", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1005", "relation": "migrated_to", "migrated_at": "2025-11-23T02:05:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/seoul/2020-2029/quest-008-taekwondo-dojo.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Боевые линии Сеула требовали независимого сюжета по обучению тхэквондо с прогрессией поясов.", "goal": "Структурировать квест, охватывающий тренировочные модули poomsae, kyorugi, breaking и финальный экзамен на чёрный пояс.", "essence": "Игрок вступает в додзё, проходит интенсивную программу и получает мастерский титул, влияющий на рукопашный бой.", "key_points": ["Пошаговая прогрессия по поясам обеспечивает ясную кривую роста навыков.", "Мини-игры с формами, спаррингом и разбиванием досок расширяют боевую систему.", "Квест связывает корейское наследие с олимпийским контекстом дисциплины."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест начинается с приглашения в авторитетное додзё. Игрок знакомится с мастером, изучает традиции, надевает добок и погружается в ежедневный режим тренировок.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "training", "title": "Тренировочные модули", "body": "Программа включает poomsae, отрабатываемые под счёт, kyorugi с адаптивным ИИ и кёкпа с системой точности ударов. Повышение уровня открывает новые комбинации.\\n", "mechanics_links": ["mechanics/combat/melee-training.yaml"], "assets": []}, {"id": "belt_progression", "title": "Прогрессия по поясам", "body": "Каждый пояс требует набора испытаний, внутренней дисциплины и экзаменов. Вариативность действий влияет на время достижения чёрного пояса.\\n", "mechanics_links": ["mechanics/progression/skill-tiers.yaml"], "assets": []}, {"id": "mastery", "title": "Экзамен на мастерство", "body": "Финальный экзамен проходит перед советом мастеров. Игрок демонстрирует poomsae, побеждает сильного соперника и выполняет серию breaking-тестов.\\n", "mechanics_links": ["mechanics/narrative/boss-challenges.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 5 000 XP, +20 к рукопашному бою, титул «Мастер тхэквондо».\\n- Доступ к фракционным контрактам боевых искусств и обучению учеников.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает корейскую ветку боевых искусств с японскими и китайскими аналогами, влияя на глобальную сеть турниров и тренировочных лагерей.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1005, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Додзё тхэквондо» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

