INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-2020-2029-myeongdong-shopping', 'Сеул 2020-2029 — «Шопинг в Мёндоне»', 'Игрок посещает ключевые магазины, собирает косметические наборы, пробует блюда и возвращается с модными покупками.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-2020-2029-myeongdong-shopping", "title": "Сеул 2020-2029 — «Шопинг в Мёндоне»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["seoul", "quest", "shopping"], "topics": ["retail-experience", "street-food"], "related_systems": ["narrative-service", "economy-service", "cosmetic-customization-service"], "related_documents": [{"id": "github-issue-1006", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1006", "relation": "migrated_to", "migrated_at": "2025-11-23T02:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/seoul/2020-2029/quest-009-myeongdong-shopping.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "В линии lifestyle Сеула отсутствовал сценарий шопинга в Мёндоне и showcase корейской косметики.", "goal": "Сконструировать квест, сочетающий торговые активности K-Beauty и дегустацию уличной еды.", "essence": "Игрок посещает ключевые магазины, собирает косметические наборы, пробует блюда и возвращается с модными покупками.", "key_points": ["Мёндон — концентратор брендов Innisfree, Etude House, The Face Shop.", "Уличная еда добавляет социальные и гастрономические сцены.", "Квест усиливает систему внешнего вида и коллекцию косметики."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок прибывает в неоновый район Мёндон в час пик. Необходимо планировать маршрут по магазинам, использовать купоны и синхронизироваться с поставками новых коллекций.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "beauty_run", "title": "K-Beauty маршрут", "body": "Ключевые точки — флагманские магазины косметики. Игрок собирает samples, участвует в мастер-классах и активирует бонусы внешности.\\n", "mechanics_links": ["mechanics/economy/retail-shopping.yaml"], "assets": []}, {"id": "street_food", "title": "Уличная еда", "body": "Дополнительные задания связаны с дегустацией tteokbokki, hotteok и odeng. Каждое блюдо даёт временные баффы и повествует о корейской кухне.\\n", "mechanics_links": ["mechanics/food/street-food-system.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 600 XP, затраты -5 000 KRW, бонус к внешности +10.\\n- Ачивка «K-Beauty эксперт» и открытие косметических линков для кастомизации.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест дополняет lifestyle-линию Сеула и связывается с событиями Gangnam и fashion-шоу, влияя на liveops-акции брендов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1006, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Шопинг в Мёндоне» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, 