INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-minsk-belovezhskaya-pushcha', 'Минск 2020-2029 — «Беловежская пуща»', 'Игрок исследует древний лес, наблюдает зубров и посещает символическое место конца СССР.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-minsk-belovezhskaya-pushcha", "title": "Минск 2020-2029 — «Беловежская пуща»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T03:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["minsk", "belovezhskaya-pushcha", "nature"], "topics": ["exploration", "history"], "related_systems": ["narrative-service", "exploration-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-minsk-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/minsk/2020-2029/quest-010-belovezhskaya-pushcha.md", "visibility": "internal", "audience": ["lore", "narrative", "live-ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик не описывал маршрут по пуще и исторический контекст соглашений 1991 года.", "goal": "Оформить экспедиционный квест с сафари на зубров и VR-реконструкцией распада СССР.", "essence": "Игрок исследует древний лес, наблюдает зубров и посещает символическое место конца СССР.", "key_points": ["Сафари и наблюдение за зубрами.", "Резиденция белорусского Деда Мороза и исторический зал соглашений.", "Награды усиливают навыки природы и культурные знания."]}, "content": {"sections": [{"id": "journey", "title": "Поездка и прибытие", "body": "Дорога в Беловежскую пущу, подготовка экспедиции и брифинг о природном наследии.\\n", "mechanics_links": [], "assets": []}, {"id": "safari", "title": "Сафари на зубров", "body": "Игрок следует маршруту, наблюдает зубров и фиксирует поведение животных. Возможно получение фотографий и данных для базы.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "Исторический контекст", "body": "Посещение резиденции Деда Мороза и места подписания Беловежских соглашений. VR-реконструкция показывает распад СССР.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "5 000 XP, +20 к параметру «Природа» и эмоциональный момент в истории. Новые ветки природы и политики в Беларуси.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Беловежская пуща", "definition": "Один из древнейших лесов Европы, объект Всемирного наследия ЮНЕСКО, место подписания соглашений о распаде СССР."}], "references": [{"title": "Belovezhskaya Pushcha National Park", "link": "https://npbp.by/en/"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Беловежская пуща» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

