INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-seoul-2020-2029-namsan-tower', 'Сеул 2020-2029 — «Башня Намсан»', 'Игрок вместе с партнёром покупает замок, оставляет послание и наблюдает 360° панораму Сеула.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-seoul-2020-2029-namsan-tower", "title": "Сеул 2020-2029 — «Башня Намсан»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T02:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["seoul", "quest", "romance"], "topics": ["romantic-events", "urban-landmarks"], "related_systems": ["narrative-service", "exploration-service", "relationship-service"], "related_documents": [{"id": "github-issue-1007", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/1007", "relation": "migrated_to", "migrated_at": "2025-11-23T02:00:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/seoul/2020-2029/quest-010-namsan-tower.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Романтическая линия Сеула не охватывала культуру замков любви на N Seoul Tower.", "goal": "Создать квест, где игрок поднимается на гору Намсан, закрепляет символический замок и наслаждается панорамой города.", "essence": "Игрок вместе с партнёром покупает замок, оставляет послание и наблюдает 360° панораму Сеула.", "key_points": ["Фуникулёр и пеший подъём предоставляют вариативность доступа.", "Замки любви формируют визуальный слой и социальные фотозоны.", "Панорама с башни усиливает эмоциональный payoff и открывает новые романтические ветки."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест стартует в нижней станции фуникулёра. Игрок выбирает способ подъёма, готовит замок с гравировкой и планирует романтический сценарий.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "ascent", "title": "Подъём на гору Намсан", "body": "Путь может проходить пешком через парк или на фуникулёре. По пути встречаются NPC-пары и продавцы сувениров, предоставляющие дополнительные истории.\\n", "mechanics_links": ["mechanics/mobility/hiking-system.yaml"], "assets": []}, {"id": "love_ritual", "title": "Ритуал замка любви", "body": "На площадке игрок покупает замок, пишет послание и крепит его к ограде. Система отслеживает количество замков и поддерживает коллекционирование.\\n", "mechanics_links": ["mechanics/relationships/romance-system.yaml"], "assets": []}, {"id": "panorama", "title": "Панорама и финал", "body": "Финальная сцена — посещение смотровой площадки башни. Игрок делает фото, активирует романтические баффы и выбирает дальнейшие планы вечера.\\n", "mechanics_links": ["mechanics/exploration/exploration-systems.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 800 XP, романтика +15, финансовые затраты -20 KRW.\\n- Ачивка «Вечная любовь в Сеуле» и доступ к романтическим ивентам на других площадках.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает романтическую ветку Сеула и соединяет её с сюжетами о свадьбах, фотосессиях и honeymoon-квестах по Азии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 1007, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Башня Намсан» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

