INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-minsk-draniki-quest', 'Минск 2020-2029 — Квест Драников', 'Игрок пробует три варианта драников и изучает секрет рецепта у бабушки.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-minsk-draniki-quest", "title": "Минск 2020-2029 — Квест Драников", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["minsk", "cuisine", "slice-of-life"], "topics": ["culinary-tour", "cultural-identity"], "related_systems": ["narrative-service", "culinary-system", "buff-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/minsk/2020-2029/quest-003-draniki-quest.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кулинарный квест не добавлял соревновательного элемента и обучающего этапа.", "goal": "Оформить гастрономический тур с дегустацией, выбором победителя и мастер-классом.", "essence": "Игрок пробует три варианта драников и изучает секрет рецепта у бабушки.", "key_points": ["Квест-id MINSK-2029-003, тип social, сложность easy, длительность 30–60 минут.", "Включает опрос местных, дегустацию и мастер-класс.", "Награды — 600 XP, -20 едди, бафф +20% HP на 8 часов и ачивка «Мастер Драников»."]}, "content": {"sections": [{"id": "tasting", "title": "Дегустация", "body": "Игрок посещает три локации и оценивает классические, мясные и грибные драник