-- Quest: canon-quest-moscow-invisible-layers
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-invisible-layers', 'Москва — Невидимые слои', 'Игрок выводит беженца из «серых слоёв» Архологии Москвы, выбирая стелс, переговоры или силовой проход.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-invisible-layers", "title": "Москва — Невидимые слои", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T19:32:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T19:32:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["moscow", "archology", "identity"], "topics": ["social_inequality", "infiltration", "humanitarian"], "related_systems": ["identity-system", "stealth-system", "negotiation-system"], "related_documents": [{"id": "canon-region-moscow-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2020-2029/quest-001-invisible-layers.md", "visibility": "internal", "audience": ["narrative", "systems", "content"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown-описание не связывало социальный конфликт архологии с системами идентификации и выбора.", "goal": "Структурировать квест о помощи беженцу с нелегальным ID, указав этапы, проверки и последствия.", "essence": "Игрок выводит беженца из «серых слоёв» Архологии Москвы, выбирая стелс, переговоры или силовой проход.", "key_points": ["Маршрут через нижние уровни, получение фальшивой идентификации и прохождение контроля.", "Выбор подкупа, хакерства или боя определяет репутацию и будущие возможности.", "Награды включают контакт с мастером ID и карту серых слоёв."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: side. Сложность: medium. Формат: solo. Длительность: 30–60 минут.\\nНаграды: 500 XP, 1 000 едди, +3 репутации у жителей серых слоёв, контакт мастера поддельных ID.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Спуститься в серые слои архологии и найти беженца, обходя патрули безопасности.\\n2. Добраться до подпольной мастерской и убедить мастера изготовить фальшивую идентификацию.\\n3. Выбрать подход к контрольной точке: подкуп, хакерство или силовое решение.\\n4. Сопроводить беженца в легальную клинику и завершить имплантацию.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "identity-system отслеживает поддельные документы и их последствия.\\nstealth-system и combat-system управляют различными подходами при прохождении контроля.\\nnegotiation-system отражает попытки договориться с охраной и подпольными мастерами.\\n", "mechanics_links": [], "assets": []}, {"id": "choices", "title": "Ключевые выборы", "body": "Подкуп охраны — стоит 500 едди, открывает контакт среди охранников и запускает ветку «Коррумпированная безопасность».\\nХакерство системы — повышает репутацию у московских хакеров и открывает доступ к подпольным рынкам.\\nСиловое решение — снижает репутацию у служб безопасности и активирует розыск на 24 часа.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Карта серых слоёв с многоуровневой инфраструктурой, патрули корп-безопасности, подпольные мастерские и клиники верхних уровней.\\nПодробно описывается контраст между нелегалами и легальной зоной архологии Москвы.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Базовые награды включают XP, валюту и репутацию, контакт мастера ID и карту серых слоёв.\\nВыбор силового пути добавляет штраф к репутации безопасности и ограничивает доступ к легальным контрактам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 272, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертировано в YAML 1.1.0, добавлены системные крючки и развилки."}, {"version": "0.1.0", "date": "2025-11-05", "author": "narrative_team", "changes": "Markdown-описание квеста \\"Невидимые слои\\"."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

