INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-shanghai-2020-2029-bund-walk', 'Шанхай 2020-2029 — «Прогулка по Бунду»', 'Игрок проходит вдоль Бунда, изучает наследие 1920-х, переправляется в Пудун и фиксирует контраст старого и нового Шанхая.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-shanghai-2020-2029-bund-walk", "title": "Шанхай 2020-2029 — «Прогулка по Бунду»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["shanghai", "quest", "bund"], "topics": ["urban-heritage", "sightseeing"], "related_systems": ["narrative-service", "exploration-service", "tourism-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/shanghai/2020-2029/quest-001-bund-walk.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Туристическая линия Шанхая не детализировала контраст исторического Бунда и футуристического Пудуна.", "goal": "Создать квест-пешеходку с осмотром колониальной архитектуры, переправой через Хуанпу и фотозаданиями skyline.", "essence": "Игрок проходит вдоль Бунда, изучает наследие 1920-х, переправляется в Пудун и фиксирует контраст старого и нового Шанхая.", "key_points": ["Исторические здания HSBC, Peace Hotel и Customs House формируют образовательный маршрут.", "Переправа через Хуанпу на пароме добавляет смену перспективы.", "Контраст с небоскрёбами Oriental Pearl Tower и Pudong Skyline усиливает эстетический эффект."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест начинается на набережной Бунда утром. Игрок получает аудиогид, маршрут и список точек интереса, отражающих эволюцию города.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "heritage_walk", "title": "Наследие 1920-х", "body": "Игрок посещает HSBC Building, Peace Hotel и Customs House, изучает интерактивные экспонаты и собирает архивные фотографии для альбома.\\n", "mechanics_links": ["mechanics/exploration/exploration-systems.yaml"], "assets": []}, {"id": "river_crossing", "title": "Переправа через Хуанпу", "body": "Паромный переход обеспечивает смену ракурса. Во время переправы активируются диалоги с NPC и исторические справки о торговом прошлом.\\n", "mechanics_links": ["mechanics/transport/ferry-system.yaml"], "assets": []}, {"id": "skyline", "title": "Контраст с Пудуном", "body": "На стороне Пудуна игрок выполняет фотозадание: зафиксировать Бунд с футуристическим фоном. Дополнительные цели связаны с вечерней неоновой подсветкой.\\n", "mechanics_links": ["mechanics/media/photo-collection-system.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 800 XP, эстетика +15, финансовых затрат нет.\\n- Ачивка «Прогулка по времени» и доступ к экскурсиям по колониальному наследию.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест дополняет историческую ветку Шанхая и связывает её с сюжетами корпоративной экспансии и культурных фестивалей на набережной.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 988, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Прогулка по Бунду» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

