INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-shanghai-2020-2029-shanghai-tower', 'Шанхай 2020-2029 — «Башня Шанхая»', 'Игрок проникает в финансовый небоскреб, обходит охрану, поднимается к техническим уровням и монтирует хакерский модуль на вершине.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-shanghai-2020-2029-shanghai-tower", "title": "Шанхай 2020-2029 — «Башня Шанхая»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["shanghai", "quest", "skyscraper"], "topics": ["vertical-infiltration", "urban-landmarks"], "related_systems": ["narrative-service", "mobility-service", "infiltration-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/shanghai/2020-2029/quest-002-shanghai-tower.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линейка Шанхая не раскрывала вертикальное проникновение в Shanghai Tower и установку нелегальных узлов.", "goal": "Создать высокорисковый квест, сочетающий social engineering, лифтовые механики и подъем на шпиль.", "essence": "Игрок проникает в финансовый небоскреб, обходит охрану, поднимается к техническим уровням и монтирует хакерский модуль на вершине.", "key_points": ["Shanghai Tower — 632-метровая спираль с ограниченным доступом.", "Лифтовая система и технические этажи раскрывают вертикальный геймплей.", "Хакерский узел на шпиле открывает экстремальные достижения и сетевые бонусы."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест стартует в деловом районе Lujiazui. Игрок изучает расписание смен охраны, подготавливает поддельные пропуска и получает\\nинструктаж от контактного лица.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "entry", "title": "Проникновение", "body": "Social engineering и поддельные пропуска позволяют войти в башню. Игрок взаимодействует с системами безопасности, разблокирует\\nлифт и избегает проверки биометрии.\\n", "mechanics_links": ["mechanics/infiltration/social-engineering.yaml"], "assets": []}, {"id": "ascent", "title": "Подъем", "body": "Самый быстрый лифт мира доставляет игрока на смотровой уровень. Здесь необходимо найти скрытый сервисный шлюз и продолжить\\nподъем по техническим лестницам и наружным конструкциям.\\n", "mechanics_links": ["mechanics/mobility/vertical-movement.yaml"], "assets": []}, {"id": "spire", "title": "Шпиль и установка узла", "body": "На высоте 632 метра игрок монтирует хакерский узел в условиях ветра и обледенения. Требуется балансировка, работа с инструментами\\nи синхронизация с сетью.\\n", "mechanics_links": ["mechanics/hacking/network-beacons.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 4 000 XP, 30 000 еддис, ачивка «На вершине мира».\\n- Повышение репутации среди хакерских фракций, но рост внимания со стороны корпораций.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает линию небоскребов с сюжетами Empire State Building и другими вертикальными испытаниями в глобальных городах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 989, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Башня Шанхая» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

