-- Quest: quest-shanghai-2020-2029-yu-garden
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-shanghai-2020-2029-yu-garden', 'Шанхай 2020-2029 — «Цифровой сад спокойствия»', 'Философский квест о природе vs технологии, где игрок исследует 400-летний сад, практикует медитацию и принимает решение о судьбе последнего природного оазиса в мегаполисе.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "meet_master_chen", "text": "Встретить Мастера Чена у Dragon Wall и узнать о проблеме с TechZen Corp, которая хочет заменить живые растения на AI-аналоги", "type": "interact", "target": "master_chen_meeting", "count": 1, "optional": false}, {"id": "explore_six_zones", "text": "Исследовать шесть зон сада: Dragon Wall, Павильон Нефритовой Скалы, Большой Рокарий, Внутренний Сад, Павильон Радостей и Зал Великолепия", "type": "explore", "target": "yu_garden_zones", "count": 6, "optional": false}, {"id": "meditation_ponds", "text": "Практиковать медитацию у трех прудов с карпами кои, синхронизируя дыхание с движением воды для обретения внутреннего покоя", "type": "interact", "target": "meditation_spots", "count": 3, "optional": false}, {"id": "tea_ceremony", "text": "Принять участие в традиционной чайной церемонии в Павильоне Радостей, освоив древний ритуал заваривания и распития чая", "type": "perform", "target": "tea_ceremony_event", "count": 1, "optional": false}, {"id": "choice_garden_future", "text": "Принять решение о судьбе сада: поддержать традицию Мастера Чена (живая природа) или согласиться с модернизацией TechZen (AI-природа)", "type": "choice", "target": "garden_future_decision", "count": 1, "optional": false}]'::jsonb, '{"experience": 1000, "currency": 0, "items": [], "reputation": {}, "flags": ["yu_garden_harmony_achievement", "mindfulness_master_title"]}'::jsonb, '[{"id": "traditional_path", "condition": "choice_garden_future == support_tradition", "name": "Путь традиции", "description": "Игрок поддерживает Мастера Чена, сад остается живым оазисом природы", "rewards": {"experience": 500, "items": ["ancient_tea_set", "jade_meditation_stone"], "flags": ["traditional_garden_preserved", "master_chen_grateful"]}, "unlocks": ["zen_meditation_skill_tree", "traditional_gardening_perks"]}, {"id": "tech_path", "condition": "choice_garden_future == support_modernization", "name": "Путь технологий", "description": "Игрок соглашается с TechZen Corp, сад становится цифровым", "rewards": {"experience": 500, "currency": 20000, "items": ["techzen_ai_garden_module", "digital_zen_chip"], "flags": ["digital_garden_installed", "techzen_corp_favor"]}, "unlocks": ["digital_meditation_skill_tree", "techzen_corp_contracts"]}]'::jsonb, '{"metadata": {"id": "quest-shanghai-2020-2029-yu-garden", "title": "Шанхай 2020-2029 — «Цифровой сад спокойствия»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-02T18:30:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["shanghai", "quest", "yuyuan-garden"], "topics": ["cultural-heritage", "mindfulness"], "related_systems": ["narrative-service", "culture-service", "meditation-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/shanghai/2020-2029/quest-003-yu-garden.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "TechZen Corp намеревается \\"модернизировать\\" древний Сад Юй Юань цифровыми растениями и AI-прудами, лишая его аутентичности.", "goal": "Помочь старому садовнику Мастеру Чену сохранить живую природу сада, противостоя корпоративному давлению и находя баланс между традицией и технологией.", "essence": "Философский квест о природе vs технологии, где игрок исследует 400-летний сад, практикует медитацию и принимает решение о судьбе последнего природного оазиса в мегаполисе.", "key_points": ["Сад Юй Юань - 400-летний оазис спокойствия среди неонового хаоса Шанхая", "TechZen Corp предлагает \\"улучшить\\" сад AI-растениями и цифровыми прудами", "Мастер Чен - традиционалист, хранящий секреты настоящего садового искусства", "Философский выбор - может ли AI-сад дать настоящее спокойствие?", "Чайная церемония и медитация дают уникальные баффы ментального здоровья"]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "meet_master_chen", "text": "Встретить Мастера Чена у Dragon Wall и узнать о проблеме с TechZen Corp, которая хочет заменить живые растения на AI-аналоги", "type": "interact", "target": "master_chen_meeting", "count": 1, "optional": false}, {"id": "explore_six_zones", "text": "Исследовать шесть зон сада: Dragon Wall, Павильон Нефритовой Скалы, Большой Рокарий, Внутренний Сад, Павильон Радостей и Зал Великолепия", "type": "explore", "target": "yu_garden_zones", "count": 6, "optional": false}, {"id": "meditation_ponds", "text": "Практиковать медитацию у трех прудов с карпами кои, синхронизируя дыхание с движением воды для обретения внутреннего покоя", "type": "interact", "target": "meditation_spots", "count": 3, "optional": false}, {"id": "tea_ceremony", "text": "Принять участие в традиционной чайной церемонии в Павильоне Радостей, освоив древний ритуал заваривания и распития чая", "type": "perform", "target": "tea_ceremony_event", "count": 1, "optional": false}, {"id": "choice_garden_future", "text": "Принять решение о судьбе сада: поддержать традицию Мастера Чена (живая природа) или согласиться с модернизацией TechZen (AI-природа)", "type": "choice", "target": "garden_future_decision", "count": 1, "optional": false}], "rewards": {"experience": 1000, "currency": 0, "items": [], "reputation": {}, "flags": ["yu_garden_harmony_achievement", "mindfulness_master_title"]}, "branches": [{"id": "traditional_path", "condition": "choice_garden_future == support_tradition", "name": "Путь традиции", "description": "Игрок поддерживает Мастера Чена, сад остается живым оазисом природы", "rewards": {"experience": 500, "items": ["ancient_tea_set", "jade_meditation_stone"], "flags": ["traditional_garden_preserved", "master_chen_grateful"]}, "unlocks": ["zen_meditation_skill_tree", "traditional_gardening_perks"]}, {"id": "tech_path", "condition": "choice_garden_future == support_modernization", "name": "Путь технологий", "description": "Игрок соглашается с TechZen Corp, сад становится цифровым", "rewards": {"experience": 500, "currency": 20000, "items": ["techzen_ai_garden_module", "digital_zen_chip"], "flags": ["digital_garden_installed", "techzen_corp_favor"]}, "unlocks": ["digital_meditation_skill_tree", "techzen_corp_contracts"]}]}, "content": {"sections": [{"id": "prologue", "title": "Пролог - Конфликт традиции и технологии", "body": "**Шанхай, 2024. Сад Юй Юань.**\\n\\n400 лет сад Юй Юань был оазисом спокойствия среди хаоса мегаполиса. Среди неоновых вывесок и голографических билбордов,\\nза древней Dragon Wall, время текло иначе. Живые бамбуковые рощи шелестели на ветру, карпы кои скользили в чистых прудах,\\nа извилистые тропинки вели к павильонам эпохи династии Мин.\\n\\nНо корпорация TechZen увидела в саду возможность. \\"Зачем живые растения, требующие ухода? AI-бамбук никогда не вянет.\\nЦифровые пруды не загрязняются. Идеальная природа - это контролируемая природа,\\" - гласила их презентация.\\n\\nМастер Чен, хранитель сада вот уже 40 лет, смотрел на голограммы идеальных, мертвых растений и чувствовал, как что-то\\nважное уходит. \\"Совершенство - это смерть. Живое всегда несовершенно. Именно поэтому оно прекрасно.\\"\\n\\nТебе пришло сообщение от Мастера Чена. Он просит о встрече у Dragon Wall. Возможно, последний раз, когда кто-то может\\nувидеть живой сад.\\n", "mechanics_links": ["mechanics/narrative/philosophy-quests.yaml"], "assets": []}, {"id": "act1_meeting", "title": "Акт 1 - Встреча с Мастером Чен", "body": "**Dragon Wall, вход в сад.**\\n\\nМастер Чен встречает тебя у Dragon Wall - величественной стены с резным драконом, защищающей сад от злых духов.\\nИли от корпораций? В 2024 грань между этими понятиями стерлась.\\n\\n\\"Благодарю, что пришли,\\" - его голос спокоен, как вода в прудах. \\"TechZen предлагает модернизацию. AI-растения,\\nцифровые пруды, виртуальная медитация. Всё идеально. Но мертво.\\"\\n\\nОн показывает тебе голограмму проекта: безупречно симметричный сад, где каждый лист точно выверен алгоритмом,\\nгде цифровые карпы никогда не болеют, где AI-вода всегда кристально чиста.\\n\\n\\"Но здесь,\\" - он касается живого бамбука, - \\"здесь дышит душа. Смотрите - вот лист пожелтел, вот ветка сломалась.\\nНесовершенство. Жизнь. Может ли AI-сад дать покой настоящей душе?\\"\\n\\nВопрос повис в воздухе. Мастер Чен предлагает тебе пройти по саду, чтобы ты сам почувствовал разницу. Прежде чем\\nпринять решение о судьбе последнего живого оазиса Шанхая.\\n", "mechanics_links": ["mechanics/dialogue/philosophy-choices.yaml"], "assets": []}, {"id": "act2_exploration", "title": "Акт 2 - Шесть зон сада", "body": "**Исследование живого сада.**\\n\\n**1. Dragon Wall (Стена Дракона)**\\nВеличественная стена с резным драконом охраняет вход. Древняя традиция гласит: злые духи могут двигаться только прямо,\\nпоэтому стена изгибается, преграждая им путь. В 2024 это работает и против корпоративных дронов-сканеров.\\n\\n**2. Павильон Нефритовой Скалы (Exquisite Jade Rock)**\\nЦентральная композиция сада - 5-метровая скала с 72 отверстиями, через которые проходит вода и воздух. Легенда гласит:\\nкаждое отверстие - это дверь в параллельный мир. Современные сканеры подтверждают аномальные квантовые флуктуации.\\n\\nМастер Чен: \\"AI может скопировать форму, но не душу камня. Эта скала видела 400 лет. Что видел AI?\\"\\n\\n**3. Большой Рокарий (Grand Rockery)**\\nЛабиринт из 2000 тонн камней, созданный вручную в эпоху Мин. Извилистые тропинки, пещеры, мосты. Никакой AI не может\\nвоссоздать хаотичность природного творения.\\n\\n**4. Внутренний Сад (Inner Garden)**\\nСамая уединенная часть. Тишина, нарушаемая только шелестом бамбука и плеском воды. Здесь нет AR-интерфейсов,\\nнет уведомлений. Только ты, природа и момент здесь и сейчас.\\n\\n**5. Павильон Радостей (Pavilion of Happiness and Longevity)**\\nЗдесь проводится чайная церемония. Древние традиции, передаваемые из поколения в поколение. AI может заварить\\nидеальный чай, но может ли он передать мудрость 400 лет?\\n\\n**6. Зал Великолепия (Hall of Heralding Spring)**\\nФинальная точка маршрута. Отсюда видны все шесть зон. Панорама 400 лет истории, запечатленная в живых деревьях,\\nкаждое из которых помнит династию Мин.\\n", "mechanics_links": ["mechanics/exploration/cultural-sites.yaml", "mechanics/exploration/zen-locations.yaml"], "assets": []}, {"id": "act3_tea_ceremony", "title": "Акт 3 - Чайная церемония", "body": "**Павильон Радостей.**\\n\\nМастер Чен готовит чайную церемонию - гунфу ча, высшее искусство чаепития. Каждое движение точное, но не механическое.\\nКаждый жест наполнен смыслом, но не запрограммирован.\\n\\n\\"Смотрите,\\" - он наливает воду в чайник, - \\"температура 85 градусов. AI скажет точно. Но я чувствую воду. Слышу,\\nкак она поёт. Это нельзя измерить.\\"\\n\\nТы участвуешь в церемонии. Выбираешь сорт чая:\\n- **Longjing (Колодец Дракона)** - зелёный чай, +15% Focus, +10% Mental Resistance, 2 часа\\n- **Tieguanyin (Железная Богиня Милосердия)** - улун, +10% Hack Resistance, +15% Stress Reduction, 2 часа\\n- **Pu-erh (Пуэр)** - ферментированный, +20% Health Regen, +5% All Stats, 1 час\\n\\nПроцесс заваривания - это мини-игра, где нужно синхронизировать дыхание с движениями, выбрать правильную температуру,\\nвремя настаивания и момент розлива. Чем точнее выполнение, тем сильнее баффы.\\n\\nМастер Чен: \\"AI может заварить идеальный чай. Но может ли он передать 400 лет мудрости в одной чашке?\\"\\n", "mechanics_links": ["mechanics/culture/tea-ceremony.yaml", "mechanics/buffs/mindfulness-buffs.yaml"], "assets": []}, {"id": "act4_meditation", "title": "Акт 4 - Медитация трех прудов", "body": "**Три пруда сада.**\\n\\nПосле чая Мастер Чен ведет тебя к трем прудам, каждый из которых олицетворяет одну из философий:\\n\\n**1. Пруд Созерцания (Dragon Pool)**\\nКарпы кои плавают по древним паттернам. Мастер Чен: \\"Смотрите, как они движутся. Никакой AI не создаст эту хаотичную\\nгармонию. Они живые. Они дышат. Они несовершенны.\\"\\n\\nМедитация: синхронизируй дыхание с движением рыб. Вдох - рыба всплывает. Выдох - погружается. 3 минуты.\\nНаграда: +10% Mental Resistance, 24 часа.\\n\\n**2. Пруд Гармонии (Lotus Pool)**\\nЛотосы цветут неравномерно - одни раскрылись, другие еще бутоны. \\"Цифровые лотосы цветут синхронно. Идеально. Мертво.\\nЗдесь каждый цветок выбирает свой момент. Это свобода.\\"\\n\\nМедитация: наблюдай за лотосами, принимая несовершенство как красоту. 5 минут.\\nНаграда: +15% Stress Reduction, 24 часа.\\n\\n**3. Пруд Покоя (Mirror Pool)**\\nИдеально гладкая вода отражает небо. \\"Вода не старается быть зеркалом. Она просто есть. Покой - это не отсутствие движения,\\nэто принятие потока.\\"\\n\\nМедитация: финальная практика. Отпусти все мысли, стань водой. 10 минут глубокой медитации с аудиогидом.\\nНаграда: Бафф \\"Покой Юй Юань\\" (+10% Focus, +10% Mental Resistance, +5% Health Regen, 24 часа).\\n", "mechanics_links": ["mechanics/wellbeing/meditation.yaml", "mechanics/buffs/mindfulness-buffs.yaml"], "assets": []}, {"id": "act5_choice", "title": "Акт 5 - Выбор будущего", "body": "**Зал Великолепия, закат.**\\n\\nТы прошел весь путь. Почувствовал дыхание живого сада. Мастер Чен стоит на вершине Зала Великолепия, откуда видны\\nвсе шесть зон. Закатное солнце окрашивает бамбуковые рощи золотом.\\n\\n\\"Теперь вы понимаете,\\" - говорит он. \\"Но выбор не за мной. TechZen предлагает миллионы. Город решает. Но вы - часть города.\\nВаш голос важен.\\"\\n\\n**ВЫБОР:**\\n\\n**[1] Поддержать традицию Мастера Чена**\\n\\"Живое всегда будет прекраснее цифрового. Сад должен дышать.\\"\\n- Сад остается живым оазисом природы\\n- Мастер Чен благодарен, открывает доступ к древним практикам медитации\\n- Награды: Ancient Tea Set (уникальный предмет), Jade Meditation Stone (+5% постоянный Mental Resistance)\\n- Разблокировка: Zen Meditation Skill Tree, Traditional Gardening Perks\\n- Последствия: TechZen недовольна, но культурное сообщество Шанхая поддерживает\\n\\n**[2] Согласиться с модернизацией TechZen**\\n\\"Технологии могут улучшить природу. AI-сад доступен 24/7, не требует ухода, идеален.\\"\\n- Сад становится цифровым, идеально симметричным, но безжизненным\\n- TechZen благодарна, предлагает контракты и технологии\\n- Награды: 20,000 еддис, TechZen AI Garden Module (кибердека модуль), Digital Zen Chip (+10% Hack Speed)\\n- Разблокировка: Digital Meditation Skill Tree, TechZen Corp Contracts\\n- Последствия: Культурное сообщество разочаровано, Мастер Чен уходит на покой с грустью\\n\\nВыбор необратим. Определяет философию персонажа: традиционализм vs технологизм.\\n", "mechanics_links": ["mechanics/choices/irreversible-consequences.yaml", "mechanics/reputation/corporate-relations.yaml"], "assets": []}, {"id": "epilogue_traditional", "title": "Эпилог (Путь традиции)", "body": "Сад Юй Юань остался живым. Мастер Чен продолжает ухаживать за растениями, обучать учеников, проводить чайные церемонии.\\n\\nТы получаешь приглашения на mindfulness-события в других азиатских садах: Киото, Сеул, Пекин. Древняя сеть хранителей\\nприроды приняла тебя.\\n\\nИногда, в моменты стресса, ты вспоминаешь Пруд Покоя. Вода. Дыхание. Момент здесь и сейчас. И стресс отступает.\\n", "mechanics_links": ["mechanics/progression/philosophy-path.yaml"], "assets": []}, {"id": "epilogue_tech", "title": "Эпилог (Путь технологий)", "body": "Сад Юй Юань теперь идеален. AI-бамбук всегда зелёный. Цифровые карпы плавают по идеальным траекториям. Вода кристально чиста.\\n\\nМастер Чен ушел на покой. Новый менеджер - AI-система, оптимизирующая визуальный опыт для максимального числа посетителей.\\n\\nTechZen благодарна. Ты получаешь доступ к их контрактам: цифровизация других культурных объектов. Хороший заработок.\\n\\nИногда, проходя мимо идеального AI-сада, ты вспоминаешь тот последний день, когда бамбук был живым. Был ли выбор правильным?\\n", "mechanics_links": ["mechanics/progression/corporate-path.yaml"], "assets": []}, {"id": "rewards_details", "title": "Детальные награды", "body": "**Базовые награды (оба пути):**\\n- 1,000 XP\\n- Ачивка \\"Гармония сада\\" (Harmony of Yu Garden)\\n- Титул \\"Mindfulness Master\\" (если завершены все 3 медитации идеально)\\n\\n**Путь традиции:**\\n- +500 XP (бонус за сохранение культуры)\\n- Ancient Tea Set (уникальный предмет, +15% Focus при использовании)\\n- Jade Meditation Stone (аксессуар, +5% постоянный Mental Resistance)\\n- Разблокировка Zen Meditation Skill Tree (новые перки медитации)\\n- Разблокировка Traditional Gardening Perks (культурные перки)\\n- Флаг: traditional_garden_preserved\\n- Флаг: master_chen_grateful\\n\\n**Путь технологий:**\\n- +500 XP (бонус за прогресс)\\n- 20,000 еддис (оплата от TechZen)\\n- TechZen AI Garden Module (кибердека модуль, +10% Hack Speed)\\n- Digital Zen Chip (имплант, +10% Focus, +5% Hack Resistance)\\n- Разблокировка Digital Meditation Skill Tree (цифровые практики)\\n- Разблокировка TechZen Corp Contracts (контракты корпорации)\\n- Флаг: digital_garden_installed\\n- Флаг: techzen_corp_favor\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml", "mechanics/economy/quest-rewards.yaml"], "assets": []}, {"id": "philosophy", "title": "Философия квеста", "body": "**Центральный вопрос:** Может ли AI заменить живую природу в создании покоя?\\n\\n**Тезис (Мастер Чен):** Совершенство - это смерть. Живое прекрасно именно своим несовершенством. AI-сад идеален,\\nно мёртв. Покой приходит от принятия несовершенства, не от его устранения.\\n\\n**Антитезис (TechZen Corp):** Технологии улучшают природу. AI-сад доступен всегда, не требует ухода, идеален для\\nкаждого посетителя. Совершенство доступно каждому через технологии.\\n\\n**Игрок решает:** Нет правильного ответа. Оба пути валидны. Выбор отражает философию персонажа и влияет на\\nдальнейшие квесты mindfulness-линии.\\n", "mechanics_links": ["mechanics/narrative/philosophy-themes.yaml"], "assets": []}, {"id": "connections", "title": "Связи с другими квестами", "body": "**Mindfulness линия (Азия):**\\n- Киотский храм Кинкаку-дзи (Япония, 2020-2029) - золотой павильон и дзен-медитация\\n- Корейский храм Бондженса (Сеул, 2020-2029) - буддийские практики\\n- Китайские сады Сучжоу (2020-2029) - классические сады и каллиграфия\\n\\n**Корпоративная линия:**\\n- TechZen Corp контракты разблокируются при выборе пути технологий\\n- Влияет на репутацию с другими корпорациями (Arasaka одобряет традиции, Kang Tao - технологии)\\n\\n**Философская линия:**\\n- Влияет на диалоги с NPC-философами в других квестах\\n- Открывает уникальные ветки в квестах с моральным выбором\\n", "mechanics_links": ["mechanics/narrative/quest-chains.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 990, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-02", "author": "content_writer", "changes": "Полная реализация квеста согласно архитектуре системы квестов:\\n- Добавлена полная структура quest_definition с objectives, rewards и branches\\n- Детализирован сюжет: конфликт традиции (Мастер Чен) vs технологии (TechZen Corp)\\n- Проработаны все 6 зон сада с детальным лором\\n- Добавлена чайная церемония с мини-игрой и выбором сортов чая\\n- Реализована медитация трех прудов с уникальными баффами\\n- Создано философское ветвление с необратимым выбором\\n- Добавлены два эпилога (традиция vs технологии)\\n- Интеграция с mindfulness-линией азиатских квестов\\nContent Writer: Issue #990\\n"}, {"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Сад Юй Юань» в формат знаний."}], "validation": {"checksum": "", "schema_version": "2.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

