INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-minsk-khatyn-memorial', 'Минск 2020-2029 — «Мемориал Хатынь»', 'Игрок посещает мемориал, слушает колокола, чтит память жертв и укрепляет культурную репутацию.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-minsk-khatyn-memorial", "title": "Минск 2020-2029 — «Мемориал Хатынь»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T03:05:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["minsk", "memorial", "ww2"], "topics": ["remembrance", "cultural-heritage"], "related_systems": ["narrative-service", "mood-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-minsk-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/minsk/2020-2029/quest-007-khatyn-memorial.md", "visibility": "internal", "audience": ["lore", "narrative", "history"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик не описывал структуру посещения мемориала и эмоциональное воздействие для игроков.", "goal": "Зафиксировать квест памяти в Хатыни с этапами и образовательным эффектом.", "essence": "Игрок посещает мемориал, слушает колокола, чтит память жертв и укрепляет культурную репутацию.", "key_points": ["Скульптура «Непокорённый человек» и 149 колоколов.", "Колокола звонят каждые 30 секунд, символизируя погибшие деревни.", "Награды выражены в опыте, скорби и связях с мемориальной линией."]}, "content": {"sections": [{"id": "arrival", "title": "В путь в Хатынь", "body": "Поездка в мемориальный комплекс в 54 км от Минска. Гид рассказывает историю сожжённой деревни и масштаб трагедии.\\n", "mechanics_links": [], "assets": []}, {"id": "memorial", "title": "Мемориальный комплекс", "body": "Игрок наблюдает скульптуру «Непокорённый человек», слушает колокола, рассматривает трубы-крыши и вечный огонь. Предусмотрена минута молчания.\\n", "mechanics_links": [], "assets": []}, {"id": "reflection", "title": "Осознание и выводы", "body": "Диалоги о памяти нации, параллели с другими трагедиями. Игрок фиксирует впечатления и может создать памятный артефакт.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "2 000 XP, эмоциональный бафф «Память и скорбь», ачивка «Мы помним» и рост репутации в мемориальной линии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Непокорённый человек", "definition": "Скульптура отца с погибшим сыном, символ сопротивления и скорби."}], "references": [{"title": "Khatyn Memorial", "link": "https://www.khatyn.by/en/"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Мемориал Хатынь» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

