-- Quest: canon-quest-moscow-network-rib
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-network-rib', 'Москва 2040-2060 — «Сетевое ребро»', 'Игрок собирает серверы, прокладывает ребро и защищает проект от NetWatch, выбирая модель доступа.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-network-rib", "title": "Москва 2040-2060 — «Сетевое ребро»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T03:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["moscow", "netrunning", "infrastructure"], "topics": ["network-reconstruction", "faction-play"], "related_systems": ["narrative-service", "netrunner-system", "economy-system"], "related_documents": [{"id": "canon-region-cis-moscow-2040-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/moscow/2040-2060/quest-021-network-rib.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик не структурировал построение «сетевого ребра» и влияние на мировую сеть.", "goal": "Описать квест создания защищённого соединения через Blackwall с последствиями для экономики и хакеров.", "essence": "Игрок собирает серверы, прокладывает ребро и защищает проект от NetWatch, выбирая модель доступа.", "key_points": ["Хакерская операция высокого уровня (DC 24).", "Варианты доступа (открытый, закрытый, монетизированный).", "Открывает глобальные контакты и пассивный доход."]}, "content": {"sections": [{"id": "planning", "title": "План и подготовка", "body": "Встреча с хакерским коллективом, обсуждение архитектуры, распределение ролей и сбор 5 серверов pre-DataKrash.\\n", "mechanics_links": [], "assets": []}, {"id": "deployment", "title": "Установка и прокладка", "body": "Установка серверов в стратегических точках Москвы. Прокладка «ребра» через Blackwall требует хакерской проверки DC 24.\\n", "mechanics_links": [], "assets": []}, {"id": "defense", "title": "Защита от NetWatch", "body": "NetWatch пытается отключить соединение. Игрок отбивает кибератаку, используя защитные ICE и боевых дронов.\\n", "mechanics_links": [], "assets": []}, {"id": "choices", "title": "Модель доступа", "body": "- Открытый доступ: для всех жителей, но сниженная стабильность.\\n- Закрытый доступ: только для хакеров, высокая стабильность.\\n- Монетизация: продавать доступ, пассивный доход 5 000 едди/месяц, риск общественного недовольства.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Награды и последствия", "body": "3 000 XP, глобальный доступ к сети, открытие торговли и контактов. Пассивный доход зависит от выбора.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 274, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Сетевое ребро» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

