INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-offline-bundle', 'Москва 2040-2060 — «Офлайн-пакет»', 'Игрок доставляет офлайн-пакет между городами из-за фрагментации NET.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-offline-bundle", "title": "Москва 2040-2060 — «Офлайн-пакет»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T03:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["moscow", "courier", "offline-data"], "topics": ["logistics", "net-fragmentation"], "related_systems": ["narrative-service", "economy-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-moscow-2040-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/moscow/2040-2060/quest-022-offline-bundle.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик не описывал варианты вмешательства в доставку и итоговые награды.", "goal": "Оформить ежедневный квест курьера с решениями о вскрытии и подмене данных.", "essence": "Игрок доставляет офлайн-пакет между городами из-за фрагментации NET.", "key_points": ["Простая доставка с возможностью взлома.", "Выбор честности, подсмотра или подмены.", "Небольшие, но повторяемые награды."]}, "content": {"sections": [{"id": "pickup", "title": "Получение пакета", "body": "Игрок получает физический носитель у отправителя, узнаёт срочность и условия доставки.\\n", "mechanics_links": [], "assets": []}, {"id": "transport", "title": "Транспортировка", "body": "Перемещение между городами: использовать быстрое перемещение или поездку. Возможны случайные встречи.\\n", "mechanics_links": [], "assets": []}, {"id": "decision", "title": "Вмешательство", "body": "- Честно доставить пакет.\\n- Подсмотреть (Взлом DC 14) и узнать содержимое.\\n- Подменить данные ради собственной выгоды.\\n", "mechanics_links": [], "assets": []}, {"id": "delivery", "title": "Сдача", "body": "Передача пакета получателю. Последствия зависят от решений: честность повышает репутацию, подмена может дать бонусы, но несёт риск.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "200 XP, 500–1 000 едди и +1 репутации курьеров при честной доставке. Подмена может дать дополнительные награды, но снижает доверие.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 274, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Офлайн-пакет» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

