INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-shanghai-2020-2029-pudong-skyline', 'Шанхай 2020-2029 — «Скайлайн Пудуна»', 'Игрок комбинирует паркур, zip-line и альпинистское оборудование, чтобы покорить три башни района Lujiazui.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-shanghai-2020-2029-pudong-skyline", "title": "Шанхай 2020-2029 — «Скайлайн Пудуна»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["shanghai", "quest", "skyline"], "topics": ["extreme-parkour", "vertical-exploration"], "related_systems": ["narrative-service", "mobility-service", "risk-management-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/shanghai/2020-2029/quest-007-pudong-skyline.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экстремальная вертикальная линия Шанхая не охватывала паркоровый маршрут по башням Пудуна.", "goal": "Определить последовательность проникновения на Jin Mao, SWFC и Shanghai Tower с финальным подъёмом на шпиль.", "essence": "Игрок комбинирует паркур, zip-line и альпинистское оборудование, чтобы покорить три башни района Lujiazui.", "key_points": ["Маршрут опирается на плотное соседство трёх гигантов и вид на Бунд.", "Zip-line между башнями обеспечивает кинематографичный момент.", "Высота и погодные условия создают существенные риски и требуют подготовки."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест стартует с получения доступа к сервисным лифтам Jin Mao Tower. Игрок использует социальную инженерную легенду для проникновения и поднимается на техническую крышу.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "traversal", "title": "Паркур-маршрут", "body": "Основная фаза — прыжки на Shanghai World Financial Center, использование zip-line и управляемый глайд на шпиль Shanghai Tower. Система адреналина влияет на баланс и точность.\\n", "mechanics_links": ["mechanics/mobility/parkour-system.yaml"], "assets": []}, {"id": "summit", "title": "Финиш на шпиле", "body": "На высоте 632 м игрок закрепляет флаг и активирует панораму. Открывается возможность транслировать событие в liveops, повышая репутацию экстремала.\\n", "mechanics_links": ["mechanics/progression/reputation-extreme.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 5 000 XP, 50 000 Eddies, титул «Покоритель Пудуна».\\n- Повышенный риск розыска и штраф при падении.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает вертикальную линию Шанхая с глобальными челленджами небоскрёбов и продолжает сюжет «Башня Шанхая».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 994, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Скайлайн Пудуна» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDE