INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-shanghai-2020-2029-chinese-opera', 'Шанхай 2020-2029 — «Китайская опера»', 'Игрок приобретает билет, наблюдает несколько актов и изучает значение масок, инструментов и боевой хореографии.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-shanghai-2020-2029-chinese-opera", "title": "Шанхай 2020-2029 — «Китайская опера»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["shanghai", "quest", "opera"], "topics": ["performing-arts", "cultural-heritage"], "related_systems": ["narrative-service", "liveops-events-service", "education-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/shanghai/2020-2029/quest-008-chinese-opera.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Культурная ветка Шанхая требовала отдельного события по традиционной китайской опере.", "goal": "Сформировать квест-посещение с акцентом на маски, символизм цветов и музыкально-акробатические сцены.", "essence": "Игрок приобретает билет, наблюдает несколько актов и изучает значение масок, инструментов и боевой хореографии.", "key_points": ["Палитра масок отражает характер персонажей и влияет на восприятие сцены.", "Антракт предлагает чайную церемонию и лекции о происхождении жанра.", "Финал демонстрирует акробатику и боевые движения, расширяя боевую систему лора."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест приглашает игрока в исторический театр. Перед началом спектакля можно посетить музей масок и получить справочник по символике цветов.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "performance", "title": "Представление", "body": "Первый акт знакомит с персонажами. Игрок фиксирует сочетания масок и учится распознавать архетипы. Вторая часть содержит акробатические и боевые сцены.\\n", "mechanics_links": ["mechanics/media/performance-tracking.yaml"], "assets": []}, {"id": "intermission", "title": "Антракт и чай", "body": "Во время перерыва игрок дегустирует чай, общается с актёрами и собирает знания для образовательных событий.\\n", "mechanics_links": ["mechanics/food/tea-ceremony.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 1 500 XP, культура +15, финансовые затраты -50 CNY.\\n- Ачивка «Знаток оперы» и доступ к liveops-фестивалям традиционного искусства.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест интегрируется с культурной линией Шанхая, а также синхронизируется с событиями Пекина и Гуанчжоу по традиционным искусствам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 995, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Китайская опера» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.object