-- Quest: canon-quest-moscow-contract-station
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-contract-station', 'Москва — Станция контрактов', 'Игрок берёт случайный контракт на станции Архологии, выбирает честную или двойную игру и формирует отношения с фиксером.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-contract-station", "title": "Москва — Станция контрактов", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T19:36:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T19:36:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["moscow", "fixer", "contracts"], "topics": ["repeatable_content", "daily_loops", "archology_life"], "related_systems": ["contract-system", "reputation-system", "procedural-mission-system"], "related_documents": [{"id": "canon-quest-moscow-invisible-layers", "relation": "unlocks"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2020-2029/quest-002-contract-station.md", "visibility": "internal", "audience": ["narrative", "systems", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Повторяемый квест станции контрактов не описывал циклы генерации и влияние на репутацию.", "goal": "Описать ежедневный контент фиксера с вариативными миссиями и последствиями двойной игры.", "essence": "Игрок берёт случайный контракт на станции Архологии, выбирает честную или двойную игру и формирует отношения с фиксером.", "key_points": ["Контракты распределяются системой процедурных миссий.", "Каждое завершение влияет на репутацию фиксеров и конкурентов.", "Доступ к станции открывается после завершения «Невидимых слоёв»."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: daily. Сложность: easy. Формат: solo. Длительность: 15–30 минут.\\nНаграды: 100–200 XP, 300–800 едди, +1 репутации у фиксеров при честном выполнении.\\n", "mechanics_links": [], "assets": []}, {"id": "mission_loop", "title": "Цикл миссии", "body": "1. Получить контракт у фиксера станции — тип задания выбирается случайно (доставка, охрана, сбор данных, устранение).\\n2. Выполнить задачу в пределах Архологии, адаптируясь к выбранному типу.\\n3. Вернуться на станцию и получить награду или разыграть двойную игру.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "contract-system генерирует задания с ежедневным обновлением.\\nreputation-system фиксирует честность или предательство и влияет на будущее качество контрактов.\\nprocedural-mission-system настраивает варианты сложности и наград.\\n", "mechanics_links": [], "assets": []}, {"id": "choices", "title": "Выборы", "body": "Честное выполнение — лояльность фиксеров, доступ к более выгодным контрактам.\\nДвойная игра — продаёт информацию конкурентам, снижая доверие заказчика, но повышая влияние альтернативных сетей.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Контекст станции", "body": "Станция контрактов расположена в центральном узле Архологии, окружена диспетчерскими, складом и терминалами перевозок.\\nПостоянное присутствие наёмников, курьеров и сетевых брокеров создаёт живой хаб для повторяемого контента.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Назначенные награды корректируются типом миссии и уровнем доверия.\\nДвойная игра открывает ветки сюжетов конкурирующих сетей, но ограничивает доступ к премиальным заказам станции.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 272, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертировано в YAML 1.1.0, описан процедурный цикл и последствия выбора."}, {"version": "0.1.0", "date": "2025-11-05", "author": "narrative_team", "changes": "Markdown-описание «Станции контрактов»."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

