-- Quest: canon-quest-moscow-ripdoc-war
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-ripdoc-war', 'Москва — Война рипдоков', 'Игрок расследует обвинения и решает, поддержать корп-клинику, подпольщиков или добиться компромисса.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-ripdoc-war", "title": "Москва — Война рипдоков", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T19:41:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T19:41:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["moscow", "ripperdoc", "factions"], "topics": ["medical_economy", "faction_conflict", "moral_choice"], "related_systems": ["faction-system", "investigation-system", "negotiation-system"], "related_documents": [{"id": "canon-quest-moscow-implant-lottery", "relation": "impacts"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/moscow/2020-2029/quest-003-ripdoc-war.md", "visibility": "internal", "audience": ["narrative", "systems", "balance"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Конфликт клиник был описан текстово и не связывался с системами фракций и проверками навыков.", "goal": "Описать эскалацию между легальной и нелегальной клиниками с точками выбора и репутационными последствиями.", "essence": "Игрок расследует обвинения и решает, поддержать корп-клинику, подпольщиков или добиться компромисса.", "key_points": ["Исследование доказательств определяет правду о контрафакте.", "Выбор стороны меняет доступ к имплантам и репутацию фракций.", "Компромисс открывает совместную программу клиник."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: faction. Сложность: medium. Формат: solo. Длительность: 1–2 часа.\\nНаграды: 800 XP, 2 500 едди, скидка 20% на импланты выбранной стороны.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Зафиксировать провокацию против нелегальной клиники и собрать первоначальные показания.\\n2. Провести расследование, используя проверки восприятия и взлома, чтобы подтвердить или опровергнуть обвинения.\\n3. Выбрать сторону конфликта или подготовить компромисс, учитывая риски боевого столкновения.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "faction-system управляет репутацией корпораций, нелегалов и жителей серых слоёв.\\ninvestigation-system предоставляет проверки восприятия и взлома базы данных.\\nnegotiation-system используется при попытке компромисса.\\n", "mechanics_links": [], "assets": []}, {"id": "choices", "title": "Решения", "body": "Поддержать легальную клинику — рейд на нелегалов, прирост репутации корпораций и штраф у населения.\\nПоддержать нелегальную клинику — оборона от корп-рейда, рост влияния серых слоёв и ухудшение отношения корпораций.\\nЗаключить компромисс — сложные переговоры, обе клиники открывают совместные скидки и будущий общий контент.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Контекст клиник", "body": "Легальная клиника находится в стеклянном башенном секторе Архологии, нелегальная скрыта в индустриальном нижнем уровне.\\nОтражены различия в оборудовании, охране и клиентуре, связывая их с социальным слоем города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "При поддержке одной стороны активируются уникальные скидки и доступ к имплантам, но закрываются предложения оппонента.\\nКомпромисс ослабляет конфликт и открывает кооперативный контент, но требует высоких проверок и наличия доказательств.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 272, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертировано в YAML 1.1.0, добавлены системные выборы и репутационные последствия."}, {"version": "0.1.0", "date": "2025-11-05", "author": "narrative_team", "changes": "Markdown-описание конфликта рипдоков."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

