INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-2040-2060-nomad-caravan', 'Москва 2040-2060 — «Караван номадов»', 'Отряд игроков нанимается охранять караван Москва — Санкт-Петербург, обслуживает транспорт и отражает угрозы до прибытия.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-2040-2060-nomad-caravan", "title": "Москва 2040-2060 — «Караван номадов»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T04:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["moscow", "nomads", "convoy"], "topics": ["travel-adventure", "convoy-defense"], "related_systems": ["narrative-service", "vehicle-system", "reputation-system"], "related_documents": [{"id": "canon-region-cis-moscow-2040-2093", "relation": "contextualizes"}, {"id": "canon-region-cis-saint-petersburg-2040-2060", "relation": "destination"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/moscow/2040-2060/quest-027-nomad-caravan.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик перечислял этапы каравана без описания угроз и командных ролей.", "goal": "Формализовать сопровождение номадского каравана с маршрутами, событиями и наградами.", "essence": "Отряд игроков нанимается охранять караван Москва — Санкт-Петербург, обслуживает транспорт и отражает угрозы до прибытия.", "key_points": ["Квест-id MOSCOW-2060-027, тип side, сложность medium, длительность 2–4 часа, рекомендуемая группа 2–4.", {"Случайные события по пути": "бандиты, поломки, погодные аномалии."}, {"Награды": "1 500 XP, 5 000 едди, +3 репутации номадов и доступ к их товарам."}]}, "content": {"sections": [{"id": "recruitment", "title": "Найм и роли", "body": "Караван ищет охрану, механика и медика. Игрок выбирает роль, знакомится с командой и готовит персональное снаряжение.\\n", "mechanics_links": [], "assets": []}, {"id": "preparation", "title": "Подготовка транспорта", "body": "Проверка грузовиков и дронов сопровождения, загрузка запасов топлива и модулей ремонта перед выходом из Москвы.\\n", "mechanics_links": [], "assets": []}, {"id": "route", "title": "Путь и столкновения", "body": "Дорога Москва → Санкт-Петербург включает погодные шторма, засады бандитов и встречи с патрулями; команда принимает решения и распределяет ресурсы.\\n", "mechanics_links": [], "assets": []}, {"id": "delivery", "title": "Завершение рейса", "body": "Доставить груз, провести диагностику транспорта и получить награду. Успех открывает номадский контент и повторяемые рейсы.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 274, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Караван номадов» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

