-- Quest: quest-dallas-2029-003-texas-bbq
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-dallas-2029-003-texas-bbq', 'Даллас — барбекю по-техасски', 'Игрок выстаивает очередь в культовом BBQ-заведении, изучает технику копчения и делится блюдом с местными, укрепляя связи.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-dallas-2029-003-texas-bbq", "title": "Даллас — барбекю по-техасски", "document_type": "canon", "category": "narrative", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-11T06:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["dallas", "food", "texas-bbq"], "topics": ["city_storylines", "food_culture"], "related_systems": ["narrative-service", "economy-service", "social-service"], "related_documents": [{"id": "master-timeline", "relation": "references"}, {"id": "quest-system", "relation": "aligns"}, {"id": "github-issue-331", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/331", "relation": "migrated_to", "migrated_at": "2025-11-22T19:45:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/dallas/2020-2029/quest-003-bbq-texas-style.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Исходный текст перечислял этапы дегустации, но не раскрывал экономические и социальные эффекты техасского BBQ.", "goal": "Показать Texas BBQ как социальное ядро района с влиянием на экономику и баффы игрока.", "essence": "Игрок выстаивает очередь в культовом BBQ-заведении, изучает технику копчения и делится блюдом с местными, укрепляя связи.", "key_points": ["Традиция копчения brisket в течение 12 часов становится игровой мини-игрой.", "Очередь и социальное взаимодействие формируют репутацию в районе.", "Дегустация открывает временный бафф здоровья и новые рецепты."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест отправляет игрока в легендарные BBQ-точки Далласа, где ожидание в очереди так же важно, как и дегустация.\\nИгрок наблюдает подготовку brisket на древесине пекана, участвует в дегустации и выбирает, с кем разделить блюдо.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Texas BBQ основан на продолжительном копчении грудинки, ребёр и сосисок. Древесина пекана придаёт фирменный аромат,\\nа очереди в Pecan Lodge или Lockhart Smokehouse подтверждают культовый статус кухни.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Найти рекомендованный BBQ-ресторан и занять очередь с возможностью мини-игр по общению.\\n2. Изучить процессы приготовления мяса и выбрать комбинацию brisket, ribs и sausage.\\n3. Решить, делиться ли блюдом с локальными NPC, что усиливает репутацию.\\n4. Получить советы pitmaster по копчению и открыть рецепт для базы знаний.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 1500 опыта, тратит 30 единиц валюты на дегустацию и активирует бафф «Сытость» +35% HP на 12 часов.\\nАчивка «BBQ Pitmaster» и доступ к локальным скидкам закрепляют участие в кулинарной линии.\\n", "mechanics_links": [], "assets": []}, {"id": "integration", "title": "Интеграции и последствия", "body": "Narrative-service обновляет кулинарную ветку города, economy-service отслеживает рост продаж brisket, а social-service\\nсохраняет отношения с жителями района Deep Ellum.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 331, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конвертирован квест «Барбекю по-техасски» с описанием экономических и социальных последствий."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

