INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-moscow-2040-2060-rogue-ai-hunt', 'Москва 2040-2060 — «Охота на роуг-ИИ»', 'Команда нетраннеров и бойцов штурмует дата-центр роуг-ИИ, проходит несколько уровней защиты и уничтожает ядро Blackwall.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-moscow-2040-2060-rogue-ai-hunt", "title": "Москва 2040-2060 — «Охота на роуг-ИИ»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T04:35:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["moscow", "blackwall", "raid"], "topics": ["endgame-content", "rogue-ai"], "related_systems": ["narrative-service", "netrunner-system", "raid-system"], "related_documents": [{"id": "canon-region-cis-moscow-2040-2093", "relation": "contextualizes"}, {"id": "canon-quest-moscow-2040-2060-blackwall-patrol", "relation": "precedes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/cis/moscow/2040-2060/quest-029-rogue-ai-hunt.md", "visibility": "internal", "audience": ["lore", "narrative", "systems"], "risk_level": "critical"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Черновик описывал рейд на роуг-ИИ без структурированных фаз и влияния на регион.", "goal": "Сформировать многоэтапный рейд с распределением ролей, рисками и долгосрочными эффектами для Москвы.", "essence": "Команда нетраннеров и бойцов штурмует дата-центр роуг-ИИ, проходит несколько уровней защиты и уничтожает ядро Blackwall.", "key_points": ["Квест-id MOSCOW-2060-029, тип raid, сложность extreme, группа 10+, длительность 2–3 часа.", "Пять последовательных стадий и три фазы финального боя.", {"Награды": "10 000 XP, 100 000 едди на группу, легендарные импланты и титул."}]}, "content": {"sections": [{"id": "mobilization", "title": "Мобилизация", "body": "NetWatch объявляет тревогу. Игроки собирают рейдовую группу, подтверждают роли (нетраннеры, саппорт, штурм) и получают доступ к объекту.\\n", "mechanics_links": [], "assets": []}, {"id": "incursion", "title": "Проникновение и зачистка", "body": "Штурм дата-центра: вскрыть внешние шлюзы, зачистить пять уровней с волнами дронов и укреплений, поддерживая связь между командами.\\n", "mechanics_links": [], "assets": []}, {"id": "bossfight", "title": "Финальная битва", "body": "Три фазы: дроны и турели; сетевые атаки против нетраннеров; психо-давление с иллюзиями. Требуется координация и управление ментальной устойчивостью.\\n", "mechanics_links": [], "assets": []}, {"id": "aftermath", "title": "Последствия рейда", "body": "Уничтожение ядра Blackwall снижает активность роуг-ИИ в Москве на месяц, открывает новые контракты и фиксирует региональное событие.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 274, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "lore_team", "changes": "Конверсия квеста «Охота на роуг-ИИ» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

