INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-singapore-2020-2029-singapore-sling', 'Сингапур 2020-2029 — «Singapore Sling»', 'Игрок посещает Long Bar, заказывает Singapore Sling, узнаёт историю напитка и участвует в традиции со скорлупой арахиса.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-singapore-2020-2029-singapore-sling", "title": "Сингапур 2020-2029 — «Singapore Sling»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["singapore", "quest", "cocktail"], "topics": ["culinary-experience", "heritage"], "related_systems": ["narrative-service", "food-and-beverage-service", "liveops-events-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/singapore/2020-2029/quest-006-singapore-sling.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Гастрономическая линия Сингапура не охватывала легендарный коктейль Singapore Sling и традиции Long Bar.", "goal": "Сформировать короткий lifestyle-квест с посещением Raffles Hotel и дегустацией исторического коктейля.", "essence": "Игрок посещает Long Bar, заказывает Singapore Sling, узнаёт историю напитка и участвует в традиции со скорлупой арахиса.", "key_points": ["Коктейль был создан в 1915 и стал символом города.", "Long Bar сохраняет колониальный антураж и уникальные правила.", "Стоимость напитка подчёркивает его туристический статус."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест начинается у входа в Raffles Hotel. Игрок знакомится с историей здания и получает приглашение в Long Bar.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "long_bar", "title": "Long Bar и заказ", "body": "Игрок проходит на второй этаж, общается с барменом, наблюдает процесс приготовления коктейля и узнаёт о составе.\\n", "mechanics_links": ["mechanics/food/beverage-preparation.yaml"], "assets": []}, {"id": "tradition", "title": "Традиция со скорлупой", "body": "После дегустации игрок участвует в традиции бросать скорлупу арахиса на пол, что добавляет очки атмосферы и открывает диалог с гостями.\\n", "mechanics_links": ["mechanics/social/social-gatherings.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 800 XP, бафф «Charisma +15% на 4 часа», финансовые затраты -30 SGD.\\n- Ачивка «Ценитель слинга» и доступ к коктейльным турам города.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест дополняет гастрономическую линию Сингапура и связывается с сюжетами о строгих законах и культурной дипломатии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 986, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Singapore Sling» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, 