INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-singapore-2020-2029-changi-airport', 'Сингапур 2020-2029 — «Аэропорт Чанги»', 'Игрок проводит время в Чанги, открывает сады разных терминалов, посещает бассейн и изучает Jewel Rain Vortex.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-singapore-2020-2029-changi-airport", "title": "Сингапур 2020-2029 — «Аэропорт Чанги»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-23T01:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["singapore", "quest", "airport"], "topics": ["infrastructure", "experiential-tourism"], "related_systems": ["narrative-service", "transport-service", "exploration-service"], "related_documents": [{"id": "github-issue-977", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/977", "relation": "migrated_to", "migrated_at": "2025-11-23T01:50:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/asia/singapore/2020-2029/quest-008-changi-airport.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Инфраструктурная линия Сингапура не демонстрировала уникальный опыт аэропорта Чанги и комплекса Jewel.", "goal": "Добавить квест-исследование с посещением Rain Vortex, тематических садов и развлекательных зон аэропорта.", "essence": "Игрок проводит время в Чанги, открывает сады разных терминалов, посещает бассейн и изучает Jewel Rain Vortex.", "key_points": ["Чанги многократно признавался лучшим аэропортом мира.", "Jewel Rain Vortex и тематические сады делают аэропорт самостоятельной достопримечательностью.", "Квест поддерживает инфраструктурные и туристические события LiveOps."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Игрок прибывает в аэропорт и получает маршрут по терминалам и комплексу Jewel. Вводятся ограничения доступа и подсказки по навигации.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "jewel", "title": "Jewel Rain Vortex", "body": "Внутри Jewel игрок наблюдает за 40-метровым водопадом, поднимается на канатные аттракционы и фиксирует AR-метки для туристического контента.\\n", "mechanics_links": ["mechanics/exploration/exploration-systems.yaml"], "assets": []}, {"id": "themed_gardens", "title": "Тематические сады", "body": "Игрок проходит через сады бабочек, кактусов и подсолнухов в разных терминалах, выполняет задания по коллекционированию и повышает эстетический рейтинг.\\n", "mechanics_links": ["mechanics/eco/eco-systems.yaml"], "assets": []}, {"id": "rooftop_pool", "title": "Бассейн и развлечения", "body": "На крыше Terminal 1 игрок посещает бассейн, кинозал и игровые зоны. Выполнение мини-игр снижает стресс персонажа.\\n", "mechanics_links": ["mechanics/wellbeing/relaxation-events.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- 1 200 XP, эстетика +15, финансовые затраты отсутствуют.\\n- Ачивка «Турист аэропорта» и доступ к транзитным лайв-ивентам.\\n", "mechanics_links": ["mechanics/progression/progression-perks.yaml"], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест связывает инфраструктурную линию Сингапура с маршрутами прилёта/вылета и туристическими цепочками в регионе.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 977, "needs_task": false, "queue_reference": [], "blockers": []}, 